insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('食品安全365','false','饲料油和食用植物油最大的区别也是来源的不同。饲料油来源自餐饮废弃油脂，这种油脂我们通常叫“地沟油”，是绝对不允许用作食用油的加工原料的。其次食用植物油在酸价、过氧化值、含铅、含砷、农药残留等各个方面都比饲料油要严格很多。','2014-10-30 08:10','1','0','0','1','fruit');
