insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('中国食品安全报','true','【果汁鲜为人知的3大真相】市面上出售的各种鲜榨果汁，相比于其他饮料，正在以其健康的概念备受欢迎。但消费者买到的“鲜榨果汁”真的是用鲜果现榨的吗?知道真相后你还会喝吗?1、鲜榨果汁多为勾兑 成本1元卖30元；2、果汁所含的营养成分比水果要少很多； 3、未经高温消毒的果汁可能是不安全的。','2015-01-04 15:20','7','0','0','3','fruit');
