insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('中国食品安全报','true','【火锅底料很鲜亮？小心辣椒有毒！】近日，南京食药监局查出一些火锅底料用了有毒工业染料“罗丹明B”。该染料是工业染色染料，具有潜在的致癌性和心脏毒性。下图右边是加过罗丹明B的辣椒，添在火锅里会让颜色变得鲜艳好看。专家提醒：不要购买颜色鲜红、容易掉色的辣椒制品！','2014-12-10 17:04','3','0','0','5','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('中国食品安全报','true','【晚上喝酸奶不为人知好处】很多人都知道牛奶含钙高，但与它比起来，酸奶中所含的乳酸与钙结合，更能起到促进钙吸收的作用。但是要想发挥酸奶补钙的最大功效，酸奶要在晚上喝最好。这是因为晚间12点至凌晨是人体血钙含量最低的时候，有利于食物中钙的吸收，人体内影响钙吸收的因素较少。','2014-12-04 10:40','0','0','6','1','milk');
