insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('中国食品安全报','true','【喝红茶、吃柑橘防卵巢癌】女性朋友们注意啦！英国东安格利亚大学对17万名25到55岁女性的饮食习惯，进行了为时30年的研究，发现常喝红茶，吃柑橘类水果能降低卵巢癌风险。红茶和柑橘中含有的黄酮成分，与降低卵巢癌发病风险相关，黄酮成分最高摄入量参试者，卵巢癌发病风险低于最低摄入量者。','2014-11-14 15:58','4','0','0','3','fruit');
