insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('健康时报','true','【酸奶水果沙拉更健康 】沙拉酱是用超过50%的油脂、蛋黄以及水快速搅拌而成的，然后再添加白醋以及其它添加剂，其实并不健康。制作水果沙拉不妨用酸奶代替沙拉酱，把各种水果准备好，去皮，切成小丁。再把切好的水果都放入碗里，淋上酸奶即可。不仅不会发胖，而且还更健康。','2015-03-23 17:57','4','0','3','4','fruit');
