insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('诸暨市场监管','true','#消费警示#【进口葡萄酒鉴别方法】购买进口葡萄酒最简单的是查看以下三个方面：一看经销商营业执照。二看中文标签，按照我国规定，所有进口葡萄酒均需加贴中文标签。三看卫生证书，每一批正规渠道进口的葡萄酒均有检验检疫部门出具的卫生证书，消费者可向商家索取查看。 O网页链接','2015-03-22 09:00','0','4','0','1','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('诸暨市场监管','true','#消费警示#【进口葡萄酒并非想象中完美】相关信息显示，2014年下半年共67批次进口葡萄酒被查出不合格，部分产品涉嫌造假，其中干浸出物、霉变、重金属超标和未提供所需证书是不合格的主要原因。无论是酒瓶包装还是灌装环境，包括进口过程中的运输环境，都存在偷工减料的现象。 O网页链接','2015-03-22 08:00','0','2','0','5','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('诸暨市场监管','true','#消费警示#【揭秘白酒市场虚假宣传“潜规则”：酒龄造假，年份成不解之谜】业内人士指出，“年份酒”已成了一些白酒厂商的“数字游戏”，怎么标都可以；“勾调酒”冒充“纯粮酿造酒”销售，在现有技术检测手段下无法被识破。花样繁多的概念成为部分不良厂商牟取暴利的工具。 O网页链接','2015-03-21 09:32','0','6','0','2','wine');
