insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('实用技能菌','false','2.用卫生纸擦拭餐具、水果。国家质检部门抽查结果表明，许多种类的卫生纸都未经消毒或消毒不彻底，上面含有大量细菌，很容易粘附在擦拭的物体上。只有经过严格消毒处理的高级餐巾纸才符合卫生标准。','2015-01-28 20:07','1','0','0','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('实用技能菌','false','《柳叶刀》杂志刊登了伦敦帝国学院科学家的一项研究，研究认为通过减少或杜绝6项健康风险因素，将有3700万人避免或延迟早死。研究称，更加健康的生活方式意味着减少三成甚至半数烟草摄入，减少10%酒精摄入，减少30%食盐摄入，减少25%的高血压患者以及控制肥胖症患者和糖尿病患者的数量。','2015-01-27 17:39','0','1','0','3','wine');
