insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('化学工业出版社大众生活分社','true','#《中医传世老偏方，男人烦恼一扫光》#【啤酒肚是负担，快饮焦三仙荷叶茶】随着年龄增长，男性体内脂肪增加并聚集于腹部，形成难看又危险的啤酒肚。可用焦三仙加荷叶煎水饮用；这种偏方来改善。购买点击右侧链接 O网页链接','2014-08-07 08:30','0','2','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('化学工业出版社大众生活分社','true','#读书#《孕妈妈这样吃最安全》【什么样的油最适合孕妈妈？】1.多选单一油，少选调和油；2.各种品牌各种类油换着吃；3.茶籽油和橄榄油可多用；4.慎用多次加热的油；5.外出就餐应减少，避免摄入地沟油。','2014-08-05 17:46','1','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('化学工业出版社大众生活分社','true','#《中医传世老偏方，男人烦恼一扫光》#【中年发福怎么办，冬瓜荷叶来帮忙】男性人到中年身体发福，赘肉多，体形慵懒。1、可用冬瓜加薏仁熬汤饮用；2、可用干荷叶泡茶饮；以上两种偏方来改善。购买点击右侧链接 O网页链接','2014-08-04 08:30','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('化学工业出版社大众生活分社','true','#读书#《孕妈妈这样吃最安全》【孕妈妈调节胃口可选水果干】孕妈妈孕初期时如胃口不好，可选水果干进行调剂。水果干没有任何添加剂，水果的营养也保留较多。水果干的碳水化合物和蛋白质含量较高，脂肪含量常在1%左右，同时是膳食纤维和矿物质的好来源。但因为浓缩了大量的糖，食用时也需注意量的控制。','2014-08-03 14:45','5','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('化学工业出版社银杏健康','true','【护肺：番茄】英国的最新研究发现，每星期吃番茄3次以上可以预防呼吸系统疾病，保护双肺免受细菌的感染。但番茄红素的含量与番茄中可溶性糖的含量是成反比的，也就是说，越是不甜的西红柿，其中番茄红素含量越高。','2014-11-24 11:28','6','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('化学工业出版社银杏健康','true','#银杏养生#【降血脂有三宝】1.洋葱中含有一种精油，可降低血脂中的胆固醇，增强高血脂病人体内纤维蛋白溶解酶的活性，对改善动脉粥样硬化有益处。2.绿豆芽虽长的普通，但降血脂效果惊人。3.苹果被誉为“食物里的全科医生”，降脂作用主要来自果胶。','2014-10-15 14:24','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('化学工业出版社银杏健康','true','#银杏美容#【女人十大天然美肤水果】：石榴（减少体内沉积的氧化胆固醇，延缓衰老）；柚子；猕猴桃；大枣（含大量维C）；苹果（补脾气、养胃阴，心血管健康保护神）；柿子（养肺护胃，清除燥火）；板栗（养胃、健脾、补肾）；梨（清热解毒、生津润燥）；橄榄（富含钙质）山楂（含丰富黄酮类及维生素）','2014-09-15 09:44','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('中国新闻周刊','true','【 吃糖的危害比吸烟还大！】糖摄入多了，会产生饱腹感，不仅带来胆固醇紊乱、癌症、心脏病、糖尿病和脂肪肝等症状，甚至会损害记忆，导致早亡。据统计，长期高糖饮食者寿命短10~20年。生活中，很多菜品、水果含糖量远高于想象，要远离这些糖可不是件容易事。怎么吃糖最健康！戳图↓via央视新闻','2015-03-23 20:30','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('科学松鼠会','true','美国2015版膳食指南专家报告日前发布，其中提到健康食谱为：多吃蔬菜、水果……“成人适量饮酒”……少吃……。如何理解其中的“适量饮酒”呢？这不是说“为了健康，应该去适量饮酒”，相反：不推荐任何人为了可能存在的健康益处去开始喝酒，或者喝超过适量的酒。详见长微博，作者 @松鼠云无心','2015-03-02 20:26','2','1','0','3','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('谣言粉碎机','true','热门 【晚上一杯奶，有个好睡眠？】“牛奶助眠”看上去相当有道理：牛奶含L-色氨酸，而L-色氨酸可以用于制造诱发睡眠的物质。但这一小份食物中含有的色氨酸量，不足以让身体激素水平发生波动。真正让人犯困的很可能是牛奶热度而非牛奶本身，还不如洗个热水澡来得直接~ O 网页链接','2015-03-02 15:50','0','0','4','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('谣言粉碎机','true','【眼泪，不能排毒】“哭出来，憋眼泪对身体不好”——也许悲伤时放声大哭能让人好过一些，但依靠眼泪“排毒”并不靠谱。研究发现，悲伤时的眼泪和因洋葱刺激出的眼泪成分差异非常小。眼泪大部分是水，和一点点无机盐和蛋白质，有一些激素蛋白和人体压力有关系，但也称不上“毒”： O 网页链接','2015-03-16 17:39','1','0','0','1','fruit');
