insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('医生加','true','【春天爱过敏？多吃藕！】日本一项研究显示：花粉过敏患者连续服用3个月莲藕做的片剂后， 60%患者过敏症状得到了有效改善。莲藕所含的黏蛋白能保护、强化呼吸道的黏膜功能，食物纤维能保护肠道、提高机体免疫力，多酚作为抗氧化物质能有效对抗导致过敏的抗体。 （人民网-健康）','2015-03-14 11:26','2','0','0','2','milk');
