insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('老年新媒体','true','#大众养生# 【早春时节养生5大要点】①晚睡早起，晚睡以不超过晚上11点为宜，早晨比冬季早起一些。②注意保暖。③入睡前一小时坚持以45度左右的热水泡足，并按摩涌泉穴。④饮食以粥汤为主，目地是补充水份。多吃新鲜的蔬菜、水果，可常食萝卜以解春困。⑤可适当散步、慢跑、打球、郊游。新浪健康','2015-03-19 09:17','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('老年新媒体','true','【春季吃“三薯”清肠又美容】①紫薯：可清除体内自由基，抗衰老。还富含纤维素，促进肠胃蠕动。②薯蓣：薯蓣即山药。能健脾益胃、助消化。山药含有皂苷、黏液质，有润滑、滋润的作用，故可益肺气、养肺阴。③马铃薯：含有丰富的膳食纤维，有助促进胃肠蠕动。用马铃薯切片敷脸还有美白的功效。新浪健康','2015-03-18 12:02','2','0','0','2','milk');
