insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('孟津女','false','HappyValentinesDay之奶油霜转印玫瑰设计来自The Contemporary Buttercream Bible比较难的部分一个是调色要调深浅不同的粉和绿着实不易若要问我每一种都怎么调的原谅我真的说不出来就是凭感觉走没有定量唯一确定的是每一种至少用到三种以上色素另一个难点是转印没做好不平整经验是油纸不行应该用油布的','2015-02-13 11:19','0','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('孟津女','false','#紫皮书SensationalButtercreamDecorating内容分享#色彩篇：蛋糕配色很重要色彩基础需知道。放送一个小秘决：大家会有这种经验奶油霜上色比较慢往往用的次数越多时间越久颜色会越深这时你再追悔色素放多了已晚一个有效办法是加完色素搅伴均匀后放冰箱冷冻变硬再拿出软化拌匀你会发现颜色比冻前深了许多','2015-01-09 10:17','0','0','1','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('孟津女','false','第一个独立完成翻糖蛋糕。做为第一个整体还是满意的糖皮自我觉良好。但也留下诸多遗憾：这两天忙家里的事各种分心有些这个心仪已久的蛋糕做的有点虎头蛇尾：Holly Leave因为原本的网纱太软硬的又没有绿色只好自己染色弄的很狼狈时间不够叶子巨大苹果花也来不及做丝带应该是细一点的纯绿色才不抢镜…','2014-12-25 14:16','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('奉贤工商','true','【种子抽检保春耕】近日， @奉贤工商 委托市农作物种子质量检测中心对南桥镇军民市场和庄行镇邬桥社区的两家种子经营户开展农作物种子进行抽检。本次抽检的样品有5个批次，种子种类分别是黄心芹、甜瓜、玉米、小青菜、鸡毛菜。待检测结果出炉，工商部门将积极采取跟进措施。','2015-03-18 10:34','2','0','0','4','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('乐活在金华','false','【D＆G、H&M、ZARA、ARMANI等服装上黑榜！】2014年，全国检验检疫机构抽检进口服装23818批，不合格品牌包括H&M、GAP、CAVALLI、OLD NAVY、FOREVER 21、ARMANI、ZARA、american apparel、DOLCE&GABBANA、芒果等。不合格原因涉及色牢度、PH值、甲醛、偶氮等。你买过这些品牌的衣服吗？','2015-03-16 10:17','1','0','0','4','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('文成县市场监督管理局','true','#文成315宣传周# #微直播# “携手共治、畅享消费”文成县2015年“3·15国际消费者权益日”宣传周系列活动已全面启动。3月10日，县食品药品综合检测中心在南田农贸市场内开展“你点题，我检测”活动，现场热烈，共送检食品29批次，包括新鲜蔬菜、水产品、肉类、干货、豆制品及面制品等，结果均为合格。','2015-03-10 18:12','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('江苏食药','true','热门 #食品安全知识# 【科学饮用牛奶】早餐喝奶，可以提供营养和能量，晚上牛奶，有助于睡眠，但值得注意的是不要空腹喝奶。空腹喝牛奶时肠蠕动加快，从而使牛奶在胃内停留时间缩短，营养素不能被充分吸收利用。因此，喝牛奶时最好与馒头、面包、玉米粥等一同食用。','2015-02-25 08:49','0','0','5','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('江苏食药','true','#怎样看食品外包装# 包装上必须标明：产品名称、配料表、净含量、产品标准号、生产日期、保质期或保存期、储存条件、制造者或经销者的名称和地址。婴幼儿配方乳粉的标签除了标注一般的标识内容外，还应有：营养成分表、适用人群及食用方法等项目，若缺少上述任何一项要谨慎购买。','2015-03-20 09:08','0','0','1','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('江苏食药','true','【别吃包装破损的网购食品】网购的预包装食品在运输途中难免出现包装破损，导致食品被细菌污染的情况，特别是熟肉制品(酱卤肉、熏烧烤肉、肉松、肉干等)、乳制品、糕点等，如果收到时食品原包装已破损，真空包装食品已出现“涨袋”现象，或者罐头出现“鼓盖”现象，最好不要吃。','2015-03-17 16:47','0','0','1','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('江苏食药','true','【食药总公布15款辣条上黑榜】想买包辣条任性一把的小伙伴们注意了，据国家食药监管总局发布抽检情况显示，多款“辣条”不合格。该类不合格产品共涉及“玉峰”、“风旺”、“阿婆传奇”等共7家生产企业的15款产品。不合格项目包含，酸价、菌落总数、柠檬黄、胭脂红等。 O 网页链接','2015-03-13 17:06','1','0','0','6','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('江苏食药','true','#食药生活#【吃火锅健康攻略】①多样的种类搭配。②慎重的选择菜品。少点海鲜内脏，多点土豆红薯③合理的食用顺序。绿叶蔬菜-菌类-海产品-肉类食品④合适的刷煮火候。食道的耐受温度为50℃至60℃，火锅浓汤的温度可高达120℃，注意烫伤。','2015-03-11 11:24','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('江苏食药','true','【水果蔬菜怎样才能洗干净？】从餐具洗涤到食材的准备，如º果能运用一些绿色非化学用剂的洗涤方法，在食品安全上也会给宝宝健康撑起保护伞。（1）柠檬醋：适用于餐具；（2）碱洗：适用于西红柿、芹菜等蔬菜；（3）柠檬汁：适用于青菜等蔬菜；（4）光照法：适用于所有蔬果。','2015-03-05 07:55','7','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('江苏食药','true','#食品安全知识#【芹菜的功效】芹菜是一种常见蔬菜，一年四季南北皆有，深受人们喜爱。除了可制作饺子、汤羹、凉拌等美食以外，芹菜还有一定药用价值。《本草纲目》记载，芹菜具有“甘凉清胃，涤热祛风，利口齿、咽喉，明目和养精益气、补血健脾、止咳利尿、降压镇静”等作用，平时常吃芹菜有益健康。','2015-02-27 14:29','1','0','0','3','fruit');
