insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师徐娟xj','true','#黄曲霉毒素对人体的危害# 黄曲霉易在粮食、油类及其制品和坚果上生长，如花生、棉籽等，干果类中的核桃、杏仁、榛子，其中花生及其制品黄曲霉素的含量最高。黄曲霉急性中毒，其毒性是砒霜的68倍，是剧毒氰化钾的10倍；而长期少量摄入黄曲霉毒素则会诱发肝癌。所以，生活中，遇到霉变的食物一定不能食用','2015-03-17 15:17','1','0','0','4','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师徐娟xj','true','#吃坚果的四大注意事项#1、要适量—带壳的坚果每天不超过一把,坚果营养虽好,但是坚果是富含淀粉或油脂的高能量食物；2、多选原味—其他口味的坚果原料可能是不新鲜的或是霉变的;3、少选开口的坚果—开口的坚果容易滋生细菌、易酸败、容易藏污纳垢、易掺假;4、吃到发苦的干果要吐掉—可能含有黄曲霉毒素','2015-03-17 15:11','1','0','0','4','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师徐娟xj','true','#四招烹调好蔬菜#1、先洗后切—防止蔬菜中的水溶性维生素和无机盐损失过多；2、急火快炒—胡萝卜素含量较高的绿叶蔬菜用油急火快炒,不仅可以减少维生素的损失,还可促进胡萝卜素的吸收；3、开汤下菜—用沸水煮根类蔬菜,可以软化膳食纤维,改善蔬菜口感；4、炒好即食—现做现吃,避免反复加热,减少亚硝酸盐','2015-03-11 11:32','5','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('安徽健康网','true','【大米配什么更营养】1.大米+豆类：补充大米中赖氨酸含量缺陷，促进蛋白质更好利用；2. 大米+蔬菜：补充足量矿物质、维生素，还能调节米饭的色彩，促进食欲；3. 大米+薯类：补充膳食纤维，促进肠道健康；4. 大米+粗粮：补充足量维生素B族，增强免疫力，还能稳定血糖，降低血脂。','2015-03-24 10:35','1','0','0','1','fruit');
