insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('安徽健康网','true','【有黑点的香蕉吃了有害吗】香蕉皮上开始出现黑点，是这个香蕉“熟透了”的表现，有少量黑点，是可以吃的，跟安全性没有关系。但是，如果香蕉表皮上的黑点太多，或已从表皮进入果肉里，这样的香蕉就属于严重腐烂，不能再吃了。','2015-03-18 14:45','5','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('平安徐州','true','【野蘑菇别乱吃 可能有毒！】近日，深圳罗某某一家父子3人自采食用野生白色伞状蘑菇后，均出现腹泻、喷射状呕吐、腹痛、头晕，医院诊断为\"急性胃肠炎、菌类中毒\"，目前两个孩子尚未脱离生命危险。 #警方提示#春季菌类生长旺盛，提醒大家千万不要采食野蘑菇！戳图看几种美丽的毒蘑菇！','2015-03-19 10:44','4','0','0','1','fruit');
