insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师-张瑜','true','#微科普#【长芽的土豆不能吃】土豆又称马铃薯或洋芋，是我国居民经常食用的一种薯类食物。当土豆长芽后，有一种有毒成分---龙葵素，可引起溶血，并对运动中枢及呼吸中枢有麻痹作用。每100g 发芽或未成熟的土豆可达30mg ~60mg 毒素，甚至达到400mg 以上。','2015-03-19 10:42','1','0','0','2','milk');
