insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师-张瑜','true','#微科普#【常喝这些饮料更聪明】❶石榴汁，富含多种独特的抗氧化剂，有助提高认知和记忆；❷茶，含抗氧化剂，每天一杯红茶可缩短大脑反应时间；❸胡萝卜和苹果汁，这两种果蔬含大量抗氧化剂、维生素和微量元素，有益大脑健康。','2015-03-16 18:24','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师-张瑜','true','#微科普#调味品是指以粮食、蔬菜等为原料，经发酵、腌渍、水解、混合等工艺制成的各种用于烹调调味和食品加工的物质。目前，我国调味品大制可分为发酵调味品、酱腌菜类、香辛料类、复合调味品类以及盐、糖等。调味品除具有调味价值外，大多也具有一定的营养和保健价值。','2015-03-16 15:49','2','0','0','1','fruit');
