insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('白痴医生','true','#膀胱癌# 亚洲人群饮用牛奶和绿茶可以降低膀胱癌发病率，摄入大量液体增加排尿次数也可以减少致癌物与膀胱上皮接触，从而降低膀胱癌风险。via @营养师顾中一','2015-03-16 18:30','0','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('白痴医生','true','【健康小知识】饿肚子时七种食物不能吃：1.柿子:易形成胃结石；2.香蕉:破坏血液中的镁钙(钙食品)平衡；3.牛奶:起不到营养滋补作用；4.豆浆:起不到营养滋补作用；5.白薯:引起烧心；6.桔子:使胃胀满、嗳气、吐酸水；7.糖:破坏机体内的酸碱平衡和各种微生物的平衡。','2015-01-25 01:14','1','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('白痴医生','true','【健康养生小口诀】1、大蒜是个宝，常吃身体好；2、一日两苹果，毛病绕道过；3、一日一枣，长生不老；4、核桃山中宝，补肾又健脑；5、胡萝卜，小人参；经常吃，长精神；6、西红柿营养好，貌美年轻疾病少；7、小小黄瓜是个宝，减肥美容少不了；8、多吃芹菜不用问，降低血压很管用。','2015-01-24 23:14','4','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('白痴医生','true','【心理技巧：心情不好吃根香蕉】德国营养心理学家研究发现，愉快的情绪，与大脑一种叫5—羟色胺的物质有关，而香蕉则含有这种能帮助大脑产生羟色胺的物质，这种物质不但能促使人的心情变得快活和安宁，甚至可以减轻疼痛，又能使引起人们情绪不佳的激素大大减少。','2015-01-24 22:14','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('琳琳营养师','true','#每天学习一点点#紫背秋葵1、鲜嫩茎叶维生素C含量较高，还含有黄铜苷，可以延长维生素C的作用2、铁含量为75毫克/每百克鲜食部分，是大白菜、萝卜和瓜类含量的二三十倍，与芹菜等含铁量高的蔬菜相当3、长期食用此菜具有治疗咳血、痛经、血气亏、支气管炎，盆腔炎及却铁性贫血等病症的功效。','2014-09-29 06:00','2','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('琳琳营养师','true','#萝卜怎么吃更健康#萝卜中明星抗癌营养素是萝卜硫素，萝卜生吃能保留更多的萝卜硫素。所以吃萝卜的小建议：1、尽量生吃、凉拌或者榨汁的方式，熟吃会降低硫苷和萝卜硫素的含量2、萝卜的辛辣味是硫苷带来的，尽量保留这种味道3、萝卜皮尽量吃掉（含硫苷和钙最丰富的地方）','2014-09-28 12:00','9','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('琳琳营养师','true','# 豆浆PK牛奶,谁更有营养#从蛋白质质量来说，牛奶中蛋白质比例搭配适当，必需氨基酸含量丰富，利用率达85%；豆浆属豆制品，大豆蛋白质中蛋氨酸有些不足，利用率稍低，达64%。（谷类食物蛋氨酸充足，豆浆搭配谷类食物，可提高蛋白质利用率）所以从蛋白质质量上说，牛奶、豆浆都属优质蛋白，牛奶更胜一筹','2014-09-02 09:00','1','0','4','2','fruit');
