insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('谷传玲营养师','true','#营养师给父亲的膳食建议#父亲检出高血压，梳理几个控盐小妙招送给他。1、起锅放盐，2、烹调时增醋、减糖、增鲜，3、减少含盐调味品用量（鸡精、酱油、蚝油、腐乳、韭菜花），4、减少高盐加工食品,5、选择低钠高钾盐,6、减少动物性食品，增加植物性食品；8在外就餐及时叮嘱，清水涮。祝父亲健康。','2014-06-15 11:10','1','0','0','1','milk');
