insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('医者小林','false','【小窍门13】哺乳后乳头涂抹红霉素眼膏能有效预防乳腺炎，效果优于护乳霜。哺乳期，乳腺导管相对开放形成细菌进入通道，乳房内充盈的乳汁是细菌良好培养基，所以哺乳期乳腺炎极为高发。红霉素眼膏可以封闭乳腺导管开口，且有抗菌作用（大环类抗生素对婴儿安全）。使用后哺乳前稍加擦拭或清洗乳头即可。','2015-03-12 08:23','0','0','1','3','fruit');
