insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('常州食药监','true','近期，我国主流电商平台上多家店铺销售一种标称为韩国cj公司生产的“肯迪醒”饮料，多数产品没有中文标识标签和说明书，或自行加贴未经审批的中文标签，同时夸大宣称具有解酒提神、缓解头痛等功效。在此提醒各位消费者，不要购买或使用此类食品，以免上当受骗，给身体健康和财产造成损失。','2014-07-10 09:25','0','1','0','2','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('常州食药监','true','7月1日上午，市药检所党员代表来到常州市儿童福利院，送去1900元的水果、奶粉、尿不湿等生活物品慰问孤残儿童。党员代表们表示，要继续将扶贫帮困的传统发扬下去，让更多的弱势群体感受到社会的温暖和关爱，希望他们能够健康快乐地生活。','2014-07-03 09:45','1','0','1','1','milk');
