insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高唐县疾病预防控制中心','true','【春天爱过敏，多吃藕】爱过敏的人注意啦！日本埼玉医科大学的研究人员，让花粉过敏患者连续服用三个月莲藕做的片剂，结果发现， 60%的患者过敏症状得到了有效改善。莲藕所含的黏蛋白能保护、强化呼吸道的黏膜功能，食物纤维能保护肠道、提高机体免疫力，多酚作为抗氧化物质能有效对抗导致过敏的抗体。','2015-03-09 15:14','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('高唐县疾病预防控制中心','true','【常喝酸奶，预防糖尿病】酸奶对健康的好处可不止是促进消化、补充蛋白质哦！美国哈佛大学公共卫生学院最新研究发现：每天28克酸奶就可以降低18%的糖尿病患病几率。研究人员认为，除了钙、镁等元素带来的益处之外，酸奶中的益生菌有助于改善肠道内环境、减少炎症发生，并调节与食欲控制有关的激素。','2015-03-09 08:15','0','0','4','3','milk');
