insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('每天学点中医养生','false','【女人一生不可缺的食物】①西红柿，抗衰抗感染。番茄红素能延缓女性衰老，预防妇科炎症；②豆类，天然荷尔蒙。异黄酮有助减轻女性更年期症状；③酸奶，润肠补钙。可改善肠道环境，补充钙质；④红肉，补铁高手。女性比男性多损失铁；⑤燕麦安神止痛。燕麦能稳定情绪，对痛经有治疗作用。','2015-03-24 10:06','2','0','1','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('每天学点中医养生','false','【健康睡眠细节】1.保证每天睡眠时间不少于7小时，不多于9小时；2.睡前1小时喝1杯牛奶有助于安神和提高睡眠质量；3.不要在床上看书或看电视，以免影响床上习惯；4.尽量在晚间11点前睡觉，帮助身体代谢；5.尽量在7点前起床，这有利于排出宿便；6.午睡不要超过30分钟。 学养生>关注 @每天学点中医养生','2015-03-24 00:51','0','0','1','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('每天学点中医养生','false','【健康便利贴】①喉干嗓哑：饮用淡盐水；服蜜梨膏；饮橘皮糖茶。②咽喉肿痛：吃梨。③口长水泡：切几片生姜入口咀嚼，可使水泡慢慢消除。④嘴唇干裂：黄瓜猕猴桃汁。⑤头发干枯：蜜枣核桃羹。⑥其他去火方法：吃柚子，喝绿豆汤可去火。饮茶以铁观音和绿茶的效果最好。 学养生>关注 @每天学点中医养生','2015-03-23 22:58','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('每天学点中医养生','false','【美容排毒减肥：蜂蜜腌柠檬】①把柠檬切成片，放在罐子里（罐子一定要洗得干干净净，用热水烫一遍）②浇上蜂蜜让每一片柠檬都沾上蜂蜜（有人用白糖，但是蜂蜜比较健康啦）③再用保鲜膜封住放在冰箱里，放上个三五天就可以了。酸酸甜甜我就喜欢~酸酸甜甜又清香超好喝，美容排毒减肥.','2015-03-23 22:30','3','0','0','1','milk');
