insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('健康养生大百科','false','【睡前不宜吃的六种食物~】1.姜，会使人精神亢奋；2.十字花科蔬菜（如西兰花，花椰菜）会引起胀气；3.辛辣食物，使体温上升，降低睡眠质量；4.汤，热量高，易导致热量超标；5.豆制品，会产生较多气体，产生腹胀感；6.酒，虽然可以让人很快入睡，但会长时间停留于浅睡期，很难进去深睡期。','2015-03-23 09:45','1','1','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('健康养生大百科','false','【世界公认的六大健康饮品】1、绿茶—明目护齿、提神醒脑、利尿解乏、促进消化。2、红葡萄酒（适量）—抗衰老、防治心血管病。3、豆浆—高蛋白、营养丰富，降低胆固醇、预防心脑血管病。4、酸奶—助消化、提高免疫力。5、骨头汤—减缓骨骼老化，促进骨骼生长。6、蘑菇汤—调节人体机能，提高免疫力。','2015-03-22 23:46','1','1','1','1','milk');
