insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容养生屋','false','【喝对蜂蜜胜过补药】喝蜂蜜五大益处：1.抗疲劳 2.抗过敏 3.消积食 4.清肠道 5.润肺止咳。不同蜂蜜养生功效各有不同：1养颜：雪脂莲蜜、野玫瑰蜜。2祛火：黄连蜜、荆花蜜、槐花蜜。3润肺：柑橘蜜、枇杷蜜、桂花蜜。4缓解失眠：龙眼蜜、枣花蜜。','2015-02-11 19:48','1','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容养生屋','false','【如何洗草莓最干净】1.用流动水连续冲洗几分钟，不要浸在水中以免农药溶出水中后再被草莓吸收；2.把草莓浸在淘米水及淡盐水中3分钟，碱性的淘米水有分解农药的作用，淡盐水使附着在草莓表面的昆虫及虫卵浮起；3.再用流动水冲净淘米水和淡盐水以及可能残存的有害物~切记:在洗草莓前不要把草莓蒂摘掉。','2015-01-15 23:52','6','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容养生屋','false','【饭后八种食物助你护肠排毒】1。海带：肠蠕动“加速器”；2。猪血：肠解毒“消化酶”；3。糙米：肠疏通“管道工”；4。蜂蜜：肠道的“养颜液”；5。酸奶：肠动力“添加剂”；6。花生：肠强健“催化剂”；7。黑木耳：肠清洁“钟点工”；8。魔芋豆腐：肠轻盈“能量元”。 推荐关注 @美容养生屋','2015-01-12 16:47','0','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容养生屋','false','【7种水果的食疗功效】1、苹果：治疗便秘；2、木瓜：可缓解消化不良；3、香蕉：过度用脑宜多吃；4、芒果：可防皱洁肤；5、蓝莓：可缓解过度用眼、眼疲劳；6、奇异果：牙龈出血时吃；7、葡萄：长期吸烟，帮你清肺排毒。 今天你吃水果了吗？','2014-12-28 21:10','6','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('健康养生知识','false','【红糖煎苹果】做法：1、锅中倒入红糖和水，小火，慢慢的将其烧融； 2、放入切片了的苹果，依然用小火，慢慢的两面煎； 3、直煎到两面色泽很深，苹果略微脱水的样子，至少要5分钟以上，就可盛起苹果，淋少许酸奶点缀装盘食用。功效：生理期抚慰女性的绝妙小甜点，MM们赶紧收藏！','2015-03-24 11:02','4','0','1','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('健康养生知识','false','【四大伤胃饮食习惯】1、剩饭加热后再吃难以消化。2、加工肉—例如咸肉、腊肠、熏肉，会增加患胃癌的风险。3、快餐食物—较硬的油炸食物如直接吞入胃内可造成胃粘膜损害。4、解酒的绿茶—绿茶的咖啡因与酒一起会刺激胃粘膜，不但不能解酒，反而会引发各类胃病。','2015-03-23 20:30','0','2','0','1','wine');
