insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('CCTV7-乡村大世界','true','#健康饮食#【春吃豌豆，养颜护眼】在春天里，吃豌豆不仅有养颜的功效，还有很好的护眼功效。 现代研究表明，豌豆中含有大量的胡萝卜素、叶黄素，对保护视神经、改善视力非常有益，尤其适合经常熬夜用眼、眼睛疲劳干涩的人吃。可以做成胡萝卜豌豆炒牛肉吃，也可以煮一锅香喷喷的豌豆饭哦。','2015-03-18 14:52','1','0','0','4','fruit');
