insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('育儿加油站','false','【怀孕初期5-8周注意事项】性生活要谨慎，注意卫生减少感染。补充叶酸（芦笋、香蕉、花菜、蛋黄、全谷类、菠菜、草莓、鱿鱼、牛奶等。没有孕吐困扰的孕妈若想补充维生素，可以先吃半片，等到怀孕中期再增量。尽量不接触有害物质，受污染的鱼类汽油油漆染料，食物添加剂，蜜饯之类的散装小零食等。','2015-03-22 19:45','2','0','1','3','milk');
