insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('饮食健康','false','【老中医顺口溜】黄瓜减肥有成效，抑制癌症猕猴桃。番茄补血养容颜，莲藕除烦解酒妙。橘子理气好化痰，韭菜补肾暖膝腰。萝卜消食除胀气，芹菜能治血压高。白菜利尿排毒素，菜花常吃癌症少。冬瓜消肿有利尿，绿豆解毒疗效高。木耳搞癌散血淤，山药益肾浮肿消。益肾强腰吃核桃，健肾补脾吃红枣。','2015-03-19 20:16','7','1','0','1','fruit');
