insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('食味米','true','#两会# #崔晶博士食味米# 【农业部副部长牛盾：我不会吃日本大米蔬菜】农业部副部长牛盾对记者表示，中国人到日本买高价大米不是大众行为，日本高价大米贸易目标是当做礼品大米来销售，中国人出国也希望购买一些当地特色品，不必提升到食品安全高度进行讨论。就他个人而言，不会吃日本的牛肉大米蔬菜。','2015-03-13 11:19','2','0','0','1','milk');
