insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('MSN名医讲堂','true','【名医讲堂第219期：糖尿病的饮食注意事项】大连市中心医院营养科主任 @营养医师王兴国 为大家解读如何“健康饮食调控血糖”，简单用三句话来总结就是：1.餐餐都要有主食；2.餐餐都要有蔬菜；3.餐餐都有蛋白质。另外，食用油选择要多样化，控制量，选择亚麻油等健康油。详见： O 网页链接','2013-01-25 09:43','1','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('MSN名医讲堂','true','【解读黄酒是否“有毒”？】黄酒近来有点忙，它有些什么问题呢？黄酒中的“致癌物”氨基甲酸乙酯是种什么物质？黄酒还能喝么？酒是越陈越好么？喝哪种酒才是最保健的？中国农业大学食品科学与营养工程学院副教授朱毅做客《名医讲堂》，给大家解读黄酒的那些事儿。 O网页链接','2012-07-11 09:36','0','7','0','2','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('MSN名医讲堂','true','【药袋装的苹果能吃吗？】果农使用加药果袋，幼果将被药袋包裹5个多月，直至成熟上市。用药袋的苹果表皮比较光滑，卖相好。但是药袋苹果存在什么样的安全隐患？中国农业大学食品科学与营养工程学院副教授朱毅做客《名医讲堂》，给大家讲讲药袋苹果的那些事儿。 O网页链接','2012-06-28 08:58','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('朱珠营养师','false','【叶黄素和玉米黄质如何保护眼睛？（二）】1、遮蔽蓝光，当视网膜中的叶黄素和玉米黄质的浓度较高时，蓝光难以穿过黄斑色素层，保护后感光细胞避免蓝光伤害；2、抵抗自由基，叶黄素可及时与感光细胞工作过程中产生的大量自由基发生反应，避免其对细胞中的而大分子物质产生氧化腐蚀','2015-03-13 18:12','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('朱珠营养师','false','#十期陪您过大年# 【干果篇——松子】1、松子中富含不饱和脂肪酸，如亚油酸、亚麻油酸等，能降低血脂，预防心血管疾病；2、所含大量矿物质如钙、铁、磷、钾等，能给机体组织提供丰富的营养成分，强壮筋骨，消除疲劳，对老年人保健有极大的益处；3、维生素E高达30%，有很好的软化血管、延缓衰老的作用。','2015-02-21 16:43','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('朱珠营养师','false','#十期陪您过大年# 【干果篇——腰果】腰果营养丰富，含蛋白质达21%，含油率达40%，各种维生素含量也都很高。腰果具有补充体力和消除疲劳的良好功效，还能使干燥的皮肤得到改善。但是，腰果含有多种过敏原，吃时要注意。另外腰果含热量高，吃多了会上火，导致咽喉肿痛，因此过年不能多吃。','2015-02-21 16:42','1','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('朱珠营养师','false','【麻酱的功效（二）】补钙，强健骨骼。在我国膳食中，钙的来源除了奶制品外，芝麻酱也是不错的钙源，每100g芝麻酱中含有1170mg的钙，是虾皮的1.1倍，豆腐的8.5倍。经常食用对骨骼、牙齿和大脑发育都有益处。','2015-02-05 20:14','0','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('朱珠营养师','false','【土豆当主食的担忧】土豆和玉米、稻米、小麦不一样，后面三种属于颗粒物，很容易脱水，从而延长保质期；而土豆属于块状物，若成体保存，很难脱水，很容易变质。这种变质指“发霉”或“发芽”。发霉就不说了，如果发芽，会产生大量的龙葵素，这是一种有毒物质。所以土豆不恰当保存很容易造成浪费。','2015-01-30 00:25','1','0','0','6','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('朱珠营养师','false','【如何正确吃年糕】年糕的血糖生成指数比较高，所以血糖波动异常的朋友建议慎食；另外年糕一次也不能吃太多，因为年糕的支链淀粉含量高，不易消化，会刺激胃分泌大量胃酸，吃多了可能会容易出现胃反酸的现象，无益于身体健康。建议吃年糕的时候可以搭配一些蔬菜，来中和支链淀粉摄入过多产生的胃部不适','2015-01-29 23:48','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('朱珠营养师','false','【为宝宝推荐的零食——水果】推荐理由：1、补充水分、矿物质的重要来源；2、有机酸还可以促进食欲，可以帮助钙、铁等营养素的吸收，预防贫血和强健骨骼；3、果胶可以促进胃肠蠕动、预防便秘；4、胡萝卜素、类黄酮等保健成分可与提高机体抵抗力，预防疾病。','2015-01-23 16:00','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('朱珠营养师','false','【健康吃烧烤的建议】1、选择室内吸烟设施好的餐馆；2、尽量选择瘦肉来烤（雪花牛肉、五花肉的脂肪很高）；3、可以适当增加海鲜比例；4、火不要过大，烤糊的部位为不要食用；5、多吃新鲜的蔬菜、增加维生素C的摄入，减少有害物质对身体的伤害；6、浅尝辄止。','2015-01-23 15:49','1','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('MSN母婴育儿','true','【5类食物孩子最好少吃】1.含糖谷物食品。多是精制面粉做成，维生素、矿物质及食物纤维含量甚少；2.酥饼。多由精制面粉制成，含油、糖及防腐剂，维生素和矿物质很少；3.蜜饯、果干、水果糖。含糖、色素、防腐剂较多；4.汉堡。多含亚硝酸盐、饱和脂肪、糖分；5.盒装果汁。配料多是水和砂糖，纯度不高。','2014-03-31 10:50','1','0','0','3','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('MSN母婴育儿','true','【莫给幼儿用香味湿纸巾】越来越多的父母喜欢给宝宝用湿巾擦手擦屁股。但是，湿纸巾含有多种添加剂，宝宝接触过多的防腐剂、酒精等化学成分，很容易引发接触性皮炎等皮肤过敏。而且大多数家长给宝宝用了湿纸巾后就不再洗手，化学成分残留在手上，对宝宝会有不利影响。','2014-03-25 09:26','0','1','0','3','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('维维营养师','true','#圣诞节# Merry Christmas~又是一年圣诞到，你会给亲爱的他（她）选择哪一款巧克力呢？牛奶的？果仁的？坚果的？统统没有纯纯的黑巧克力好！最好选可可含量60%左右的，既兼顾了营养（可可中的多酚物质有助心血管健康），同时恰如其分的微苦口感及可可脂带来的丝滑触感也为会为你增添一份好心情呢~','2013-12-24 14:18','0','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('维维营养师','true','俗话说＂上床萝卜，下床姜＂。这意思是早上吃姜。姜确有驱寒的功效，适合渐凉的天气，可早上吃这辛辣的姜着实难以接受呢。不妨早饭时准备小碟泡姜，就着米粥吃，很清爽哦。也可煮成红糖姜水饮用。暖暖的出门，一天都精神百倍 我在: 2 翠微路','2013-10-16 14:46','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('维维营养师','true','【为什么要断夜奶？】1、宝宝80%的生长激素都是在夜里尤其是在后半夜分泌的，常起夜喂奶会影响生长激素的分泌。2、断了夜奶，处于哺乳期的妈妈可以得到更好的休息，有利于保证充足、高质量的乳汁。3、宝宝喂奶次数过多，会导致宝宝体重超重。可先将临睡前的那顿奶量适当增加，与前面一顿的时间适当延长','2013-09-04 11:57','0','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('冉婷美食与营养','true','儿童要常吃当季水果：儿童经常吃当季的水果，能让身体更健康，而且也能让皮肤更水灵。水果中富含丰富的维生素、矿物质、可溶性膳食纤维等，特别是深色水果中的胡萝卜素含量很丰富，对于学习看书的孩子们来说，能保护眼睛。而且儿童经常吃水果还能预防便秘，同时也能增加孩子的抵抗力。','2013-05-01 09:53','5','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('冉婷美食与营养','true','牛奶补钙营养又健康：牛奶中有丰富的蛋白质，而且各种氨基酸比例符合人体的需要，消化和吸收都比较好，能达到90%以上。牛奶中的钙是平时膳食中最好的钙的来源。是乳质钙，吸收和利用率很高。所以每天吃奶类更健康。','2013-04-26 17:49','0','0','3','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('冉婷美食与营养','true','浓茶解酒可取吗：浓茶，民间有浓茶解酒的说法，实际上，我们喝醉后，再喝上一大杯浓茶，浓茶中的茶碱会使我们血压上升，反而增加头痛的感觉，并没有起到解酒的作用。所以说，浓茶解酒是不可取的。所以平时我们饮酒要适量，这样才能健康身体。','2013-04-26 17:48','0','2','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('冉婷美食与营养','true','为什么每天饮奶有利于预防骨质疏松：奶类不仅钙的含量高，而且钙磷的比例合适，其中还有维生素D、乳糖、氨基酸等促进钙吸收的因子，更是提高了我们身体对钙的吸收利用率。奶类来源的钙更容易吸收，可以使骨密度的增加更持久。所以每天饮奶骨骼更健康。','2013-04-16 14:45','0','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('冉婷美食与营养','true','为什么每天吃大豆好：在植物类的食物中，大豆就含丰富的优质蛋白，其中还有必须脂肪酸、B族维生素、维生素E等。豆类食物中营养丰富，更有多种健康功效，尤其对老年人和心血管患者是很好的一类食物，建议摄入量30g-50g 。而且豆类价格相对肉类食物较低，建议大家每天都要食用。','2013-04-15 15:46','2','0','0','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('冉婷美食与营养','true','牛奶价格高并不一定好：现在有很多儿童牛奶系列中添加了牛磺酸、钙等营养成分。或是有些牛奶中突出了，不含抗生素、无激素喂养、无污染喂养等。从而提高了产品的价格。但从营养价值来说，再高档的牛奶也是以普通牛奶的营养成分为基础的，只是少数成分指标有所差异，对健康来说没有明显变化。','2013-04-05 19:18','0','0','5','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('冉婷美食与营养','true','生吃蔬菜要注意什么：吃火锅的时候，有些人喜欢将蔬菜生吃，认为这样有利于营养素的吸收，这种生吃的好处在于营养素破坏的少，而且和肉类搭配吃还起到了降火的效果。不过，在生吃蔬菜的时一定要保证其安全和卫生，把蔬菜在锅中稍微煮5-10秒，这样既留住了营养，又保证了我们的饮食安全。','2013-04-05 17:15','4','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('杨庭营养师','true','　　 #果粒酸奶营养价值如何？#市面上果粒酸奶的很多果粒成分来自于添加果酱。而果酱经过高温煮制，已经不能和新鲜水果的营养价值相媲美。即便是真果粒，由于酸奶的保质期是21天，放了三个星期的果粒，营养价值也会打折扣，浓郁的香味可能来自水果香精。所以还是建议买原味哒 2北营房中街','2015-02-07 00:56','2','0','3','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('杨庭营养师','true','金箔入酒实质上是在白酒中加入色素，使白酒的颜色摆脱单一的透明无色，按标准添加不会有健康隐患，当然也不会有什么所谓的养生功效。金箔入酒引热议个人认为主要原因在白酒尤其是高档白酒本身价格不菲，值此年关，若是借金箔炒作，恐会让人想到奢靡之风。 O本质是一种着色剂 专家解释\"金箔入酒\"','2015-02-05 15:03','0','7','0','4','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('赵彩霞营养师','true','#霞姐妈妈营养小屋#把健康带回家，给爸妈现榨果汁，放心又营养！','2015-02-15 21:40','1','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师武媚','false','发表了博文《《这些年关于牛奶的那些事儿》》《这些年关于牛奶的那些事儿》牛奶是天然食品中为数不多的营养全面的代表。但也有各种各样的传言，误导着大家：空腹不能喝牛奶！低脂牛奶更健康！牛奶不能加糖，不能加果 O《这些年关于牛奶的那些事儿》','2015-02-03 13:01','0','0','7','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师武媚','false','#为什么建议喝酒前吃点主食？#2、不能消化的糖类在肠道细菌的作用下发酵产生短链脂肪酸，有广泛的解毒与保健作用。若空腹直接喝酒，酒精对肝脏有直接毒性作用，吸收入血的乙醇在肝内代谢，造成其氧化还原状态的改变，从而干扰脂类、糖类和蛋白质等营养物质的正常代谢，同时也影响肝脏的正常解毒功能。','2014-12-05 17:18','0','1','0','3','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师武媚','false','#为什么建议喝酒前吃点主食？#1、主食中碳水化合物分解成葡萄糖，葡萄糖经过糖醛酸途径生成的葡萄糖醛酸是一种重要的结合解毒剂，在肝脏中可与酒精、细菌毒素、重金属砷等结合，以消除或者减轻这些物质的毒性或生物活性，从而减少对肝脏的损伤，达到解毒的作用。','2014-12-05 17:15','0','1','0','3','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('宋兵兵营养师','true','#健康我最大#昨天和闺蜜去吃火锅，她吃的麻辣的，吃到一半随即她喝了两杯水。问到后才知道她舌头感到火烧火燎的，遇到这种情况，最有效的方法是喝点香油。因为辣椒素是脂溶性的，用水很难冲掉。但是，让辣椒素溶解在香油中，很容易把舌头解放。记住这个小妙招奥','2015-03-03 10:51','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师尹双双','true','【产后喝汤水有何讲究？】乳母每天摄入的水量与乳汁分泌量密切相关，摄入量不足时，可使乳汁分泌量减少，故乳母每天应多饮汤水。鱼汤、鸡汤、肉汤营养丰富，含有可溶性氨基酸、维生素和矿物质等营养成分，不仅味道鲜美，还能刺激消化液分泌，改善食欲，帮助消化，促进乳汁的分泌。','2014-09-11 08:59','0','0','3','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师尹双双','true','【戒烟、禁酒】孕妇吸烟或经常被动吸烟，烟草中的尼古丁和烟雾中的氰化物、一氧化碳可能导致胎儿缺氧和营养不良、发育迟缓。孕妇饮酒，酒精可以通过胎盘进入胎儿血液，造成胎儿宫内发育不良、中枢神经系统发育异常、智力低下等。为了生一个健康的婴儿，孕妇孕妇应继续戒烟、禁酒，并远离吸烟环境。','2014-08-28 15:24','0','1','0','1','milk');
