insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('台州公安','true','【抽假烟的危害 】反正都是烟，假烟抽一下也没关系？其实，这是一种认识的误区。假烟因受原材料、制作工艺、包装存储、运输条件等限制，其有害物质含量往往比真烟要高得多，对人体危害很大。那么，问题来了，只是更换了过滤嘴和包装，但烟草是真的，那会不会对身体有影响呢？ L老瓶换新酒，真烟变假烟','2015-03-16 09:49','0','1','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('李苏华医生','false','【洋葱你所不知的八大惊人功效】●黄皮洋葱：肉质微黄，柔嫩细致，味甜，辣味居中，适合生吃或者蘸酱，耐贮藏，常被制作成脱水蔬菜。预防骨质疏松　　洋葱所含的斛皮黄酮有类似雌激素的作用，可和女性荷尔蒙或骨质疏松药物相匹敌，有研究报告，每天吃200- （分享自 @凤凰健康） O网页链接','2015-03-21 23:27','4','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('南湖晚报','true','【3月21日“世界睡眠日”：你若睡好，便是晴天】今年世界睡眠日主题为“健康心理 良好睡眠”。中国睡眠研究会调查显示，中国成年人失眠的发生率高达38.2%。而据查，近六成安徽人易失眠。专家说，数羊、喝酒不能促眠，泡脚、半杯牛奶或一本哲理性的书有助睡眠。 O网页链接','2015-03-20 20:42','0','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('人民网健康卫生频道','true','【当心：筷子这样用会沾满病菌！】筷子可以说是我们餐桌上使用最频繁的餐具之一，家用筷子由于运用频率高，且长期用水洗刷，致使筷子的含水量特别高，很容易成为细菌（如黄色葡萄菌、大肠杆菌等）生长的温床。动图展示 O 网页链接 关注人民网健康微信号：rmwjkpd，扫扫二维码加入我们。','2015-03-23 17:45','1','0','0','2','milk');
