insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('汤臣倍健营养家','true','#小Note大能量#【红葡萄酒的7大健康功效】1、预防龋齿；2、有助睡眠：红酒中的褪黑激素可以让你睡得更香更平静；3、保护心脏：红酒中的白藜芦醇可防止脂肪在动脉里过量积累；4、预防感冒；5、美容瘦身；6、预防癌症：红酒中的槲皮素可预防肺癌；7、延年益寿。','2015-03-18 11:30','0','4','0','3','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('汤臣倍健营养家','true','#小Note大能量#【吃辣椒保护肺部健康】吃辣椒的好处大家已经知道有很多，如青辣椒中的维生素C含量非常高，是等量橘子的4倍；辣椒素可以促进人体内褐色脂肪细胞分解…..但是你知道吗？辣椒素还有去除肺部细胞表面尼古丁的功效,所以如果你有吸烟的习惯，或者你常常被迫去吸二手烟，可以酌情吃点辣椒哦！','2015-03-16 10:23','6','0','0','2','fruit');
