insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('陕西美食探店','true','#生活小常识#【如何降低肝癌风险】①注意饮用水安全。A.一些饮用水常被多氯联苯、氯仿等污染，B.蓝绿藻是致癌植物，C.华支睾吸虫感染可刺激胆管上皮增生。②扔掉霉变食物。尤其是霉变的玉米、花生，这些食物中含有黄曲霉毒素。③远离肥胖，避免糖尿病，肥胖和糖尿病是诱发肝癌的重要危险因素。','2015-03-08 19:31','1','0','0','7','milk');
