insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('孕妆日记孕妇护肤品','true','【如何让选择适合宝宝的早期喂养方式】 @崔玉涛 ：婴儿体重下降不超过7%就要坚持母乳喂养。生理母乳喂养过程是先喂细菌再喂乳汁的有菌喂养过程，能够促进孩子肠道正常菌群建立，促进免疫系统成熟，预防过敏发生。切不可使用含有消毒剂的湿纸巾擦洗乳房，更不可弃去一些乳汁再喂养婴儿。','2015-03-19 20:21','0','0','2','1','milk');
