insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('实用微博小百科','false','【番茄对预防摄护腺癌的效果特别突出】而愈来愈多的研究显示，番茄对降低乳癌、消化道的癌症、子宫颈癌、膀胱癌和肺癌的风险也有某种程度的帮助。番茄中的茄红素、维生素C和潃J萝卜素都是极佳的抗氧化剂，加上丰富的钾、菸硷酸、维生素B6和叶酸共同作用，可以维护心血管的健康。','2015-03-17 09:07','4','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('实用微博小百科','false','【少吃加工食品，尤其是各种加工休闲零食】维生素A、维生素E、维生素B2、维生素C缺乏，是食道癌、胃癌高发区居民饮食的共同特点。多食用新鲜蔬菜水果这些富含维生素的食物，可以降低疾病风险。而加工食品通常含有大量调味料、盐、糖、添加剂等，对健康无益。','2015-03-17 08:37','2','0','0','3','fruit');
