insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师张萃婧','true','热门 #微健康# 【吃水果制品可以替代新鲜水果？】确实，水果经过加工后的水果制品有了易保存、易携带等诸多优点，但是却会损失水果中的营养成分，比如水溶性的维生素-维生素C和对肠道有益的膳食纤维等等，很是可惜。所以建议大家吃新鲜应季的水果，一些没有被空气或农药污染的可以带皮吃的水果也尽量带皮吃。','2015-03-10 23:21','7','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师张萃婧','true','#微健康# 【B族维生素-烟酸】 烟酸在人体内有三个作用：1.构成辅酶；2.是葡萄糖耐量因子的组成成分；3. 保护心血管-降低血胆固醇、甘油三酯及β-脂蛋白浓度及扩张血管。大剂量烟酸对复发性非致命的心肌梗死有一定程度的保护作用。故充足的烟酸摄入很重要哦，特别是以玉米为主食地区，应该额外补充烟酸。','2015-03-24 00:03','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师张萃婧','true','#微健康# 【喝酒御寒？】酒精属于纯能量的东东，喝进人体后产生的热量不能转化成脂肪储存起来，只能是转化成能量消耗掉，比如发汗就是能量消耗的表现，也就是为什么有人说喝酒可以御寒了。','2015-03-23 00:02','0','1','0','1','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师张萃婧','true','#坚持阅读一百天# 【第12天】今天浏览营养界大咖范志红老师的博客，看到一篇关于爆米花的博文，其中写到食用爆米花的危害，以前是铅超标的问题，现在是对身体有害的添加剂太多，诸如人造奶油、香精、色素这些。有反式脂肪酸的问题，有影响鋅的吸收的问题等等。其实我们吃的棉花糖合唱不是呢','2015-03-22 23:58','0','0','1','4','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师张萃婧','true','#微健康# 【核黄素】理论上讲，从每日膳食可获得1.2mg（成年女子）的量，可以满足《中国居民膳食指南》推荐的膳食参考摄入量，如30g猪肝+100g扁豆+50g鸡蛋（一个鸡蛋50g）=（0.6+0.45+0.15）mg维生素B2。但是如果已经出现维生素B2的缺乏如上皮损坏，还需要通过膳食补充剂补充一段时间。','2015-03-20 21:47','1','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师张萃婧','true','#微健康# 【核黄素】也就是维生素B2，水溶性维生素，所以遇水就溶解进去。富含维生素B2的食物都有哪些呢？比如，动物性食物中肝、肾、心肌等含量很高，其次为蛋、奶类；植物性食物中如大豆和各种绿叶蔬菜。《中国居民膳食指南》每日参考摄入量（RNI）为：成年男子1.4mg/d；成年女子1.2mg/d。','2015-03-20 14:40','2','0','0','1','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师张萃婧','true','#微健康# 【维生素B1】中国营养学会推荐的维生素B1膳食营养素参考摄入量（RNI）为成年男子1.4mg/日，成年女子1.3mg/日。按照《中国居民膳食指南》推荐量做每日膳食举例：50g瘦猪肉（0.27mgB1）+20g葵花籽仁（0.38mgB1）+米饭四两（0.22mgB1）+30g大豆（0.1mgB1）+50g小米（0.26mgB1），共计1.23mg B1。','2015-03-19 22:59','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师张萃婧','true','#微健康# 【维生素B1】含维生素B1较丰富的食物有葵花籽仁、花生、大豆粉、瘦猪肉，其次为小麦粉、小米、玉米、大米等谷物类。维生素B1溶于水，在酸性水溶液里较稳定，对光和氧也比较稳定，但是在碱性环境易被氧化失活，不耐热。所以烹饪时应注意少用碱，比如熬粥时。另外，尽量减少淘洗次数以免流失过多','2015-03-19 22:37','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师张萃婧','true','#微健康#【植物化学物质】大豆里的植物化学物如大豆卵磷脂和植物固醇有降低血脂的作用。大豆卵磷脂可以降低血清胆固醇，植物固醇在人体血液内会占胆固醇的位子，所以植物固醇越多，进入血液的胆固醇就越少，因此可以控制血液胆固醇的浓度，从而起到降血脂的作用。那谁不是脂肪肝么还不多吃点儿？！','2015-03-17 23:42','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师张萃婧','true','#微健康#【喝豆浆会变得娘娘腔？】一些蓝银认为吃豆子会让男人变得娘娘腔，因为大豆异黄酮，他们很怕豆子中的这个东东让他们因为雌激素的原因变得娘们儿，所以不敢喝豆浆吃豆制品。没错，这个东东确实有等同雌激素的作用，在人体内它们只是占着雌激素的位子，还不至于可以改变人的性别！','2015-03-17 23:20','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师张萃婧','true','#微健康#【蔬菜】按品种不同，营养价值差异很大，所以建议大家尽量选择进食多种不同蔬菜，选新鲜应季的，深色蔬菜占一半以上。如果选择马铃薯、芋头、莲藕、山药等含淀粉较多的蔬菜时，主食的量要适当减少哦','2015-03-13 23:01','6','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师张萃婧','true','#微科普#【蔬菜-深色蔬菜】蔬菜按颜色分为深色和浅色，深色蔬菜的营养价值一般由于浅色蔬菜。深色蔬菜指深绿色、红色、橘红色、紫红色蔬菜，富含胡萝卜素尤其β-胡萝卜素，是中国人维生素A的主要来源。所含其他多种色素物质如叶绿素、番茄红素、花青素等，还有一些芳香物质，颜色丰富，还可促进食欲。','2015-03-13 22:49','8','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师张萃婧','true','#微健康#【婴幼儿补铁+维生素C】在给婴幼儿补铁的同时,最好在添加米粉的同时加点儿果汁作为辅食，因为维生素C可以促进铁的吸收。','2015-03-12 23:51','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师张萃婧','true','#微健康#【吃蔬菜水果可控制体重】蔬菜和水果含水量大，富含膳食纤维，吃它们进肚子，不但可以增加饱腹感，还可以加速食糜通过肠道，这样控制了进食量，也减少了吸收的量；再之，蔬菜和水果给我们提供的能量相对主食较低，吃得量虽然比主食多，但是摄入的能量低，有利于维持健康体重，预防超重和肥胖。','2015-03-11 23:44','6','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师张萃婧','true','#坚持阅读一百天#【第2天】蔬菜和水果防癌作用：蔬菜和水果富含膳食纤维，膳食纤维能缩短食物残渣通过肠道的时间，并可以同食物残渣中潜在的致癌物、次级胆汁酸、短链脂肪酸结合，促进其排出。这样就减少了肠道中有害物质进入人体血液的可能性，也就对人的健康没有威胁了。','2015-03-11 23:18','4','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('北京平谷健康教育','true','“香浓牛奶加纯正果汁，15种营养，一步到位！来不及吃早餐，就喝XXXX！”中国营养学会副理事长翟凤英听到这则广告后表示，“这么说不恰当”。我们从饮料瓶上的成分表就能看出，其中添加剂高达11种，偶尔当饮料喝一下无可厚非，可如果您真的信了广告的话，把它当早餐，那您就太相信广告了。','2015-03-16 08:42','1','0','1','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('北京平谷健康教育','true','1.化妆品瓶口爱藏细菌，梳妆台上的化妆品，我们每天基本都会使用1-2次，每次使用时不停地打开盖子再盖上盖子，化妆品瓶口很容易沾染灰尘，因此很容易滋生细菌。所以小歪建议在梳妆台上常备一瓶酒精棉，或是棉签加酒精，时不时给瓶口或喷口消个毒哦。','2015-03-09 08:39','0','2','0','2','wine');
