insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('hi小宝君','true','花园紫米饭。一般西兰花造型圣诞树以土豆泥打底，这次推荐到紫米和薏仁蒸熟，然后调味一些盐，香油。插上西兰花，西红柿，黄辣椒装点成一颗花园圣诞树。整个制作过程非常有趣。很适合跟小朋友一起操作，味道也不赖，营养上来讲低脂健康。我自己吃的那份，饭团中间捏了一团肉松和火腿肠。 #每日食谱#','2015-03-09 11:21','2','0','0','1','milk');
