insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师Heidi','true','#为什么吃蔬菜#1.提供丰富的矿物质和膳食纤维，保证人体营养所需，2.能量低预防肥胖，3.提供具有保健功效的植物化学物质4.水分多，补充水分。','2015-01-17 22:37','1','0','0','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师Heidi','true','#锌的作用和补充来源# 1.锌是儿童生长发育的必需元素。 2.锌有助于改善食欲。 3.锌有助于皮肤健康。4.食物主要来源是贝壳类海产品、红色肉类、动物内脏，其次干果类、谷类胚芽和麦麸是锌的丰富来源；','2015-01-05 18:32','1','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师Heidi','true','#维生素C的作用和食物来源# 1.有助于维持皮肤和黏膜健康。 2.有助于维持骨骼、牙龈的健康。 3.可以促进铁的吸收。4.极好来源是蔬菜水果 维生素C有抗氧化作用。','2015-01-05 18:10','2','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师韩梅','false','#痛风患者的安全食品1# 1.牛奶是牛的乳腺细胞分泌的体液，里面没有细胞结构， 没有细胞核，也就没有遗传物质，没有核酸，所以没有嘌呤，痛风患者可以放心的食用。','2015-03-20 15:58','0','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师韩梅','false','#蛋清蛋黄与营养#从感官上看，蛋清越浓稠，表明蛋白质含量越高，蛋白的品质越好。 蛋黄的颜色有深有浅，从淡黄色至橙黄色都有。蛋黄颜色与其含有的色素有关。蛋黄中主要的色素有叶黄素、玉米黄质、黄体素、胡萝卜素及核黄素等。蛋黄颜色深浅通常仅表明色素含量的多寡少，与营养价值关系不大。','2015-03-05 13:49','1','0','0','3','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师韩梅','false','#节后宜吃粗糙食物#粗糙食物之所以粗糙，是因为此类食物中含有丰富的非水溶性纤维，比如粗粮、芹菜杆和白菜帮等。可就是膳食纤维，能帮助人们预防便秘、防止肠癌、有利金属离子排出，帮助身体排毒，减轻体重。如果你在吃高脂菜肴的时候担心发胖，可以吃这些因为这是粗糙食物发挥效力的最佳时刻。','2015-03-05 10:44','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师韩梅','false','#健康吃零食要点#1、根据个人的身体情况选择适合个人的零食，一般应选择营养价值高的，如水果，奶制品，坚果等。2、应选择合适的时间。两餐之间可适当吃些零食，以不影响正餐食欲为宜。晚餐后2-3小时也可吃些零食，但睡觉前半小时不宜再进食。3、零食的量不宜太多，以免影响正餐的食欲和食量；','2015-03-05 09:56','1','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('陈成O_o','false','【我为“霾都”挚爱研发臻爱茶】他梦想能用爱和责任，为挚爱但不能在一起的人创造出一款极致臻享的预防PM2.5致肺癌的健康饮品。你觉得他会成功吗？这款浸润着至纯至真的爱的臻爱茶，最终会采用刺梨、西洋参和中宁枸杞最为有机原材料吗？ M 防霾养颜【臻爱茶】快到碗里来！','2015-01-19 10:53','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师徐墨书','false','#玉米油# 玉米油也称为粟米油、玉米胚芽油。其脂肪酸组成与葵花籽油类似，单不饱和脂肪酸和多不饱和脂肪酸的比例约为1：2.5，特别富含维生素E，还含有一定量的抗氧化物质。它降低胆固醇的效能优于大豆油、葵花油等高亚油酸的油脂，也具有一定的保健价值。玉米油可以用于炒菜，也适合用于凉拌菜。','2014-12-19 14:39','2','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师徐墨书','false','#奶类与骨质疏松#奶类含钙量高，钙、磷比例适当，还有维生素D、乳糖、氨基酸等因子，钙吸收利用率更高，是膳食优质钙的主要来源。奶类来源的钙可使骨密度增加更为持久，并可对抗随年龄增长而导致的骨密度下降和由骨质疏松导致的骨折。尤其雌激素水平明显下降的绝经期后妇女，能更好改善钙营养状况。','2014-12-13 15:23','0','0','1','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师徐墨书','false','#脂溶性维生素#维生素A可以帮助保护皮肤和肠道健康，来源有深绿色、黄色、橙色和红色的蔬菜和水果以及蛋黄、肝脏和肉类；维生素D促进钙的吸收，食物来源有动物肝脏、蛋黄和瘦肉、海鱼、乳酪、坚果和海产品；维生素E保护细胞免受损伤，食物来源有绿色蔬菜、植物油、花生和蛋黄。','2014-12-13 12:40','3','0','1','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师徐墨书','false','#你会用泡腾片补充维生素C么？#首先，泡腾片的水温不宜过高，不宜在空气中放置太久，否则极容易高温破坏或氧化影响补充效果；其次，泡腾片为了达到起泡升腾的效果，会加入用于崩解起泡的盐类物质，以及糖、酸、香精色素等配料，这些成分对健康无益。建议补充维生素C还最好选用天然水果和蔬菜！','2014-12-09 16:15','2','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('四川共青团','true','#小青注意# 【 吃糖的危害比吸烟还大！】糖摄入多了，会产生饱腹感，不仅带来胆固醇紊乱、癌症、心脏病、糖尿病和脂肪肝等症状，甚至会损害记忆，导致早亡。据统计，长期高糖饮食者寿命短10~20年。生活中，很多菜品、水果含糖量远高于想象，快告诉小伙伴怎么吃糖最健康！戳图↓','2015-03-24 12:03','1','0','0','1','milk');
