insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('中国经济网','true','【除了辣条 还有哪些\"5毛零食\"危害孩子健康?】由于售价低廉、口味香浓、包装醒目,\"五毛食品\"深受孩子喜欢。近年来,多地曝光\"五毛零食\"质量问题,诸如脱氢乙酸、山梨酸、甜蜜素等添加剂超标,私人\"黑作坊\"非法加工,贴虚假QS标示。除了辣条,还有哪些\"5毛零食\"危害孩子健康? O网页链接','2015-03-23 08:32','1','0','0','6','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('蔡政稳营养师','true','#逢年过节：警惕“儿童饮料综合征”#英国南安普敦大学最近发现了一种名为“儿童果汁饮料综合症”的疾病，于过多地饮用果汁饮料，而导致发育迟缓、食欲不振、多动、健康状况不良、过敏等。跟果汁中大量的糖类以及人工合成的香精色素、香精都有关。但这些又是孩子喜爱喝的，春节将至，各位妈妈要注意了。','2015-01-30 16:29','3','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('范丽娜的生机美食日记','true','【全食物早餐】新年刚过，又迎来忙碌的开学季，我家的营养早餐也不能中断哦！自制坚果牛奶面包，涂抹上自制的醇香芝麻酱，配煎蛋、火腿、奶酪片、黄瓜好吃营养的三明治搞定，在配一杯鲜牛奶！一餐营养又健康的儿童早餐就完成啦','2015-03-04 09:47','1','0','2','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('范丽娜的生机美食日记','true','感叹生命成长的神奇，旅游前种下得小麦草，旅游回来已长得郁郁葱葱！嫩嫩的细芽看着都让人有种欣欣向荣的喜感～小麦草富含叶绿素，有助造血，可协助肝脏净化血液综合细胞内的毒素，清热解毒之功效，结合水果、坚果制成最新鲜的、最天然的小麦草精力汤，美好的早晨，从一杯满满的果蔬能量精力汤开始啦！','2015-02-08 09:51','1','0','0','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师成彦南','true','8点赶回家给孩子补过生日，蛋糕胚子是东东姐提前两天做好的，草莓是爱婴寄过来的，我就只剩打法奶油和铺水果了，等搞定后才发现我忘准备蜡烛了 ，拿手指饼干充当蜡烛，娃说蛋糕好吃，吃了好多！这次蛋糕是最健康的，全部健康食材，纯手工制作！ 再次谢谢东东和爱婴！','2015-01-08 21:18','2','0','1','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('健康生活专家-老丁','true','【四种搭配护眼】1、菠菜+坚果；2、洋葱+猪肝（洋葱和猪肝都有很好的营养保健作用，它们含有丰富的维生素A，不仅能保护眼睛，维持正常视力，防止眼睛干涩、疲劳）；3、枸杞子+猪肝（将枸杞子和猪肝一起煲汤具有清热、消除眼涩、消除因熬夜出现的黑眼圈的功效。）4、绿色蔬菜+彩色水果。','2015-03-24 10:54','4','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('健康生活专家-老丁','true','【给外食族的健康忠告】①拒绝一切路边摊；②食物尽可能多样；③拒绝重口味；④拒绝过油菜；⑤每顿有蔬菜；⑥热水涮菜；⑦带馅的只选素馅；⑧连锁的中餐、日式餐馆好于西方的洋快餐；⑨每天至少一顿饭自己做，以粗粮和蔬菜为主。','2015-03-24 09:54','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('健康生活专家-老丁','true','【做到“十个点”助你长寿】1.饭要稀一点：把饭做成粥，健脾养胃、生津润燥，对益寿延年有益。2.饭菜香一点：适当往菜里多加些葱、姜等调料。3.蔬菜多一点。4.菜要淡一点。5.荤菜少一点。6.质量好一点。7.食物热一点。8.吃得慢一点。9.数量少一点。10.晚餐早一点。','2015-03-24 06:54','1','0','0','1','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('健康生活专家-老丁','true','【早餐要丰富多样】“早吃好，晚吃少”，营养早餐是健康生活的开始，一顿满分早餐应该具备5个条件：①早餐中有淀粉类主食；②有奶类、鸡蛋、肉或鱼、豆浆或豆腐至少一种；③早餐有蔬菜水果；④早餐有坚果；⑤烹饪方法健康。美好的一天从早餐开始。','2015-03-24 05:54','2','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('健康生活专家-老丁','true','【红瓤柚子独特功效】柚子堪称冬季的最佳水果，它具有健胃消食、化痰止咳等功效。其中，红壤柚子所含的天然色素保健作用更强，黄酮类化合物对预防心血管疾病有益处，而β胡萝卜素和番茄红素是世界上公认的强抗氧化剂，能提高免疫力、延缓衰老。另外，红肉蜜柚还有助于保护视力。','2015-03-24 03:24','2','0','0','4','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('Acymer宁静的夏天','true','【清晨第一杯水你喝对了吗？】①白开水：排毒瘦小腹，一般人最好的选择；②盐水：加快肠胃蠕动排出毒素，便秘者适用；③柠檬水：增加食欲，但长期饮用会引起钙流失，造成胃酸分泌过多；④蜂蜜水：不适合早晨喝，更适合睡前，中医有云：朝朝盐水，晚晚蜜汤；⑤牛奶：空腹喝牛奶，营养流失。','2015-03-21 09:45','0','0','2','1','milk');
