insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('壹健康官微','true','【吃鸡蛋 搭配煮熟的豆浆】大豆中含有一些胰蛋白酶抑制物，其活性就是抑制胰蛋白酶的消化作用，从而降低对蛋白质的吸收。但豆浆煮熟后，胰蛋白酶抑制物的活性就会被破坏，也就不会影响蛋白质的消化。因此，煮熟的豆浆和鸡蛋一起吃，完全不存在营养损失。 来自壹健康： O网页链接','2015-03-16 17:00','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('中国循环杂志官博','true','【每日小酒超2杯，中风提早5年】研究发现，大量饮酒也是中风的一大祸害。中年人每天大量饮酒（约236 ml）的含酒饮料，中风的风险比每日少量饮酒（约59 ml）者增加34%。到60～75岁时，大量饮酒者中风的风险更高，是少量饮酒者的2倍。 O网页链接','2015-02-07 08:58','0','2','0','2','wine');
