insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('程程dreaming','true','【绿茶×酸奶】 选择绿茶粉，把10克的绿茶粉放到200克酸奶中搅拌均匀后即可食用，喝绿茶酸奶可以起到排毒的作用，并能缓解便秘，防止小肚腩生成，以及还具有抗辐射的功效，想去掉小肚腩的MM可以多喝点绿茶酸奶。','2015-03-23 12:46','0','0','4','1','milk');
