insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容健康小妙招','false','【香蕉滋润抗皱面膜】材料：香蕉半根、鲜奶适量、蜂蜜1勺、面粉2勺。做法与用法：将香蕉连皮切段、捣碎，加入鲜奶、面粉和蜂蜜后均匀搅拌至糊状，涂在脸部约5~10分钟后洗净即可。功效：滋润皮肤，有效对抗皱纹，并抑制黑色素形成，让肌肤容光焕发。适用肤质：干性皮肤货有轻微细纹的皮肤。','2014-06-23 22:08','3','0','0','1','fruit');
