insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('李世鹏营养师','true','#糖尿病患者不宜饮酒# 不宜饮酒。因为酒中所含的酒精不含其他营养素只供热能，每克酒精产热约7千卡，长期饮用对肝脏不利，而且易引起血清甘油三脂的升高。少数服磺脲类降糖药的病人，饮酒后易出现心慌、气短等反应。注意，使用胰岛素的患者空腹饮酒易引起低血糖，所以，为了病人的安全还是不饮酒为佳。','2015-03-13 14:49','0','3','0','1','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('李世鹏营养师','true','#血糖高要多吃菜# 建议糖尿病患者每天吃菜一斤以上，特别是绿叶蔬菜，不仅可以提供多种矿物质和抗氧化物质，减少眼底和心脑血管系统并发症的风险，还能提高饱腹感，对于糖尿病人的好处极大。','2015-03-01 08:06','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('杭州科技职业技术学院学生膳管会','false','#杭科院每日健康# 【韭菜】春天气候冷暖不一，需要保养阳气，而韭菜最宜人体阳气。韭菜含有挥发油、蛋白质、脂肪和多种维生素等营养成分，有健胃、提神、强肾等功效。春韭味道尤为鲜美。春韭吃法多样，既可佐肉、蛋、虾、墨鱼等，又可做蒸包、水饺的馅料。炒绿豆芽或豆腐干时加些春韭，格外芳香可口。','2015-03-24 07:09','3','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('杭州科技职业技术学院学生膳管会','false','#杭科院每日健康# 【菌类·蘑菇】蘑菇有高蛋白、低脂肪、高纤维素的特点，其中维生素B1、尼古丁酸等的含量高于其他菌类。它还有一种抑制肿瘤生长的物质，有抗癌作用。挑选蘑菇时应选择色白、茎粗、伤痕少的。由于容易氧化变色，洗净后在清水中浸泡以隔绝空气，但不适用于铁质容器浸泡，否则将变成黑色。','2015-03-19 07:32','3','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('杭州科技职业技术学院学生膳管会','false','#杭科院每日健康# 【菌类·香菇】香菇除了富含维生素B2等B群以外，还含有可以预防胆固醇过高引起的动脉硬化等的倍塔-葡萄糖苷酶，具有能阻挡紫外线功能并富含生成维生素D的麦角酶等多种营养。在选购香菇的时候，中等个头且均匀，菌伞肥厚，盖面细滑，菌柄短而粗壮的香菇是购买时的首选。','2015-03-17 07:57','1','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('杭州科技职业技术学院学生膳管会','false','#杭科院每日健康# 【皮肤干燥吃什么·石榴汁】石榴的果实色彩缤纷，红如玛瑙，白若水晶。用石榴榨汁，喝起来酸甜爽口，并且还有\"御饥疗渴，解酲止醉\"之功。除了对身体有很好的\"润\"作用之外，石榴汁还是一种比红酒、番茄汁等更有效的抗氧化果汁，并且效果持久。','2015-03-16 05:35','2','1','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('杭州科技职业技术学院学生膳管会','false','#杭科院每日健康# 【防止静电】在饮食方面，可适当增加维生素A.C.E和酸性食物的摄取，如胡萝卜、卷心菜、西红柿及香蕉。苹果、猕猴桃等含有大量维生素C，带鱼、甲鱼可增加皮肤弹性和保湿性，具有良好的除静电功能。','2015-03-12 07:35','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('杭州科技职业技术学院学生膳管会','false','#杭科院每日健康# 【吃火锅时喝点酸奶】吃火锅时最好喝点酸奶，因为酸奶不但营养丰富，而且可以保护胃肠道黏膜。酸奶中含有乳酸菌，它有酸化肠腔，抑制腐败菌生长，减落其在肠道中产生毒素的作用，可以预防因火锅的油腻以及其他因素引起的腹泻、腹痛等。','2015-03-11 06:46','0','0','4','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('杭州科技职业技术学院学生膳管会','false','#杭科院每日健康# 【吃完烧烤后吃香蕉】烧烤类食品会产生较多的苯并芘等致癌物。军事医学科学院食品与营养系博士表示，最新研究发现，香蕉能在一定程度上抑制苯并芘的致癌作用，保护肠胃。','2015-03-10 07:17','2','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('杭州科技职业技术学院学生膳管会','false','#杭科院每日健康# 【莲藕，维生素B1冠军】无论做汤、清炒还是凉拌，莲藕都是养生食谱上大受欢迎的食材。其含有丰富的维生素B1，有助于缓解疲劳、促进新陈代谢和消化，还有美肤的作用。此外，莲藕还含有维生素C、钙、铁等无机盐，具有抗氧化作用的多酚类物质。','2015-03-05 07:26','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('杭州科技职业技术学院学生膳管会','false','#杭科院每日健康#【吃辣椒10功效，助你健康 】1.开胃消食；2.暖胃驱寒；3.促进血液循环；4.肌肤美容，辣椒能促进体内激素分泌，改善皮肤状况；5.降脂减肥，辣椒素能加速脂肪分解；6.止痛散热。7.抵抗癌症，辣椒素能加速癌细胞死亡；8.保护心脏；9.降低血压；10.对糖尿病有利。','2015-03-04 08:00','4','0','0','6','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('杭州科技职业技术学院学生膳管会','false','#杭科院每日健康#  【开心果】美国癌症研究协会发表的研究发现，开心果含有γ生育酚可以降低肺癌风险。其中大量的白藜芦醇，含量仅次于红酒，能抗癌与预防心血管疾病。其中的植物固醇还能保护心血管健康。怎么选?选择无调味开心果，并且一天不要超过一把。','2015-01-23 08:59','0','1','0','3','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('杭州科技职业技术学院学生膳管会','false','#杭科院每日健康#【抗衰老食品：圆白菜】圆白菜亦是开十字花的蔬菜，维生素C含量很丰富，同时富含纤维，促进肠胃蠕动，能让消化系统保持年轻活力，并且帮助排毒。','2015-01-19 11:44','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('杭州科技职业技术学院学生膳管会','false','#杭科院每日健康#【“黑皮香蕉”最排毒】很多人看到香蕉皮变黑了，就以为是坏掉了，其实这种外皮发黑的香蕉更有利于排毒。 研究表明，香蕉买后搁置至外皮发黑时，使人体内产生的白血球数是刚买来香蕉能帮助产生的白血球数的5倍。因此，外皮发黑的香蕉更能起到抗癌的作用。但那种烂掉的香蕉是不能吃哟!','2015-01-16 10:20','7','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('杭州科技职业技术学院学生膳管会','false','#杭科院每日健康#【草莓】草莓中所含的胡萝卜素是合成维生素A的重要物质，具有明目养肝作用；对胃肠道和贫血均有一定的滋补调理作用；草莓除可以预防坏血病，防治动脉硬化，治疗冠心病；可在体内可吸附和阻止致癌化学物质的吸收，具有防癌作用；可以自然平和的清除体内的重金属离子。','2015-01-15 09:01','3','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('杭州科技职业技术学院学生膳管会','false','#杭科院每日健康#【抗衰老食物：酸奶】酸奶不仅有助于消化，还能有效地防止肠道感染，提高人体的免疫功能。与普通牛奶相比，酸奶脂肪含量低，钙质含量高，还富含维生素B2，这些元素都对人体大有裨益。','2015-01-14 14:55','0','0','4','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('杭州科技职业技术学院学生膳管会','false','#杭科院每日健康#【感冒发烧吃什么好 】高烧大量出汗后，如果不吃不喝很容易导致电解质紊乱，最好先吃点西瓜或喝些鲜榨西瓜汁，它们可以帮助退烧、补充体液、消肿利水。','2015-01-13 10:27','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('杭州科技职业技术学院学生膳管会','false','#杭科院每日健康#【牛蒡疏风 】中医认为，冬天“风邪”盛行，吃牛蒡可疏风散热，帮助人体抵御风邪的侵袭。每100克牛蒡的胡萝卜素含量达到了390毫克，远高于胡萝卜，其蛋白质和钙含量在根茎类蔬菜中也是“傲视群雄”。牛蒡还含有过氧化物酶和一些抗菌成分，有一定的抗衰老和降血糖作用。','2015-01-08 08:39','2','0','0','1','fruit');
