insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('肿瘤医生张宗岐','true','发表了博文《啤酒混着喝更易得肝癌》　　近年来流行的“红酒、白酒、啤酒混喝”现象让健康专家很是忧虑。因为欧美等发达国家已有大量研究显示，酒精与肝癌有着密切的关系，而且，饮混合酒的人，患肝癌的机率比饮温和 O啤酒混着喝更易得肝癌','2015-03-09 09:13','0','7','0','1','fruit');
