insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('中粮五谷道场','true','#健康不随便#【多吃胡萝卜有助于缓解电脑工作者的营养】拯救双眼：胡萝卜、菠菜：长时间注视电脑荧光屏，视网膜上的感光物质视紫红质消耗加快，若未能及时补充其合成物质维生素A和相关营养素，会导致视力下降、眼痛、怕光、暗适应能力降低等等。/网络图','2015-01-14 13:10','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('世健联合健康管理','true','【抗癌水果排行榜】1.草莓，预防白血病、再生障碍性贫血等；2.柑橘类水果，防治消化道癌；3.猕猴桃，阻断体内致癌的“亚硝胺”生成；4.梨，适宜于鼻咽癌、喉癌、肺癌患者；5.杏，具有杀灭癌细胞作用；6.葡萄，抑制癌细胞恶变；7.苹果，抑制癌细胞增殖。8.无花果，适宜于咽喉癌、腺癌、宫颈癌、膀胱癌者','2015-03-20 13:54','7','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('世健联合健康管理','true','【健康养生口诀】1、大蒜是个宝，常吃身体好；2、一日两苹果，毛病绕道过；3、一日一枣，长生不老；4、核桃山中宝，补肾又健脑；5、胡萝卜，小人参；经常吃，长精神；6、西红柿营养好，貌美年轻疾病少；7、小小黄瓜是个宝，减肥美容少不了；8、多吃芹菜不用问，降低血压很管用。报名热线 0755-83691616','2015-03-19 16:24','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('世健联合健康管理','true','【春季吃“三薯”清肠又美容】①紫薯：可清除体内自由基，抗衰老。还富含纤维素，促进肠胃蠕动。②薯蓣：薯蓣即山药。能健脾益胃、助消化。山药含有皂苷、黏液质，有润滑、滋润的作用，故可益肺气、养肺阴。③马铃薯：含有丰富的膳食纤维，有助促进胃肠蠕动。用马铃薯切片敷脸还有美白的功效。','2015-03-18 10:04','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('凤凰财经','true','【质检总局公布2015年首批进口食品黑榜】嘉仕堡巴氏杀菌乳被检出大肠菌群超标，亨氏番茄汁等还未入境就已过保质期，雀巢牛奶巧克力冰淇淋因未提供所需证书被销毁，上海宜家家居有限公司进口的两批次斯耐普斯风味伏特加因未提供所需证书被销毁，农心牌辣味虾条被查出包装不合格。 O网页链接','2015-03-23 17:33','1','0','1','3','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('英才杂志','true','【医药行业的“科技闯入者”】随着移动设备开启了评估健康的新方式，科技行业对医疗健康领域兴趣日益加深，苹果的ResearchKit平台就是一个最新例证。对传统医药公司来说，苹果闯入令他们不安。来自硅谷及其他地区的新加入者会强行挤入药品研发过程吗？C','2015-03-24 11:10','2','0','0','2','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('全民健康互动','false','【灸督脉可补气血】一些人阳气不足，冬天会畏寒畏冷、手脚冰凉，山东省中医院针灸科副主任杨佃会建议，可尝试用督灸来补气血。先用酒精或者温开水将督脉处擦拭几遍，从大椎穴到腰俞穴（见下图）贴上厚厚的姜片，然后在姜片上放艾柱点燃。一般情况下一个月一次督灸就足以达到保健的功效了。','2014-07-31 18:33','0','1','0','2','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('全民健康互动','false','【盘点柠檬的神奇养生功效】柠檬含丰富的维生素C，能防止牙龈红肿出血，具有很强的抗氧化作用，对促进肌肤的新陈代谢、延缓衰老及抑制色素沉着等十分有效。柠檬中维生素C和维生素P，能增强血管弹性和韧性，可预防和治疗高血压和心肌梗塞症状。青柠檬中含有一种近似胰岛素的成分，可以降低血糖值。','2014-07-14 17:32','4','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('全民健康互动','false','【女性健康养生】：1.每餐尽量品种多、数量少；2.菜肴讲究荤素搭配；3.每天不忘喝牛奶吃乳制品；4.肉食每天至少吃50克,但不超过100克；5.每天都吃鱼，且吃鱼应多于其他家禽；6.每天一个鸡蛋同米饭一起吃；7.做菜少放食盐；8.每天至少吃一次豆类食品。','2014-07-11 17:15','0','0','2','1','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师秦慧玲','true','【赴宴学会6招不损健康】①多喝开水解油腻。②盘饰青菜多多进食。③汤汁鱼肉少量摄取。④酒加冰块降低浓度。⑤最好选择菊花茶。⑥饭后不要马上吃水果。提示：外出赴宴回家后的饮食一定要保持清淡，少油少盐，多吃蔬菜水果、豆制品等，以保持整体的饮食平衡。','2014-10-31 09:23','4','1','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师秦慧玲','true','【十种食品不宜常吃】①油炸食品：热量高，含有较高的油脂和氧化物质。②罐头类食品。③腌制食品。④加工的肉类食品(火腿肠等)。⑤肥肉和动物内脏类食物。⑥奶油制品。⑦方便面。⑧烧烤类食品：含有强致癌物质三苯四丙吡。⑨冷冻甜点，包括冰淇淋、雪糕等。⑩果脯、蜜饯。','2014-10-28 11:58','1','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师秦慧玲','true','【学生应该多吃的8种食物】1、胡萝卜：加快大脑的新陈代谢 2、菠萝：提高学习效率 3、白菜：能减少紧张情绪 4、柠檬：有助于集中精力 5、海虾：长时间保持精力集中 6、洋葱头：改善大脑氧的供应状况 7、香蕉：含血清素给大脑供养 8、海带：缓解学习后的疲劳感。','2014-10-27 10:10','6','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师秦慧玲','true','【生活百科】平常吃苹果，有许多人喜欢连皮一起吃，但现在许多保鲜技术让苹果表面残留化学物质不易清洗。介绍一种清洗方法：苹果过水浸湿后，在表皮放一点盐，然后双手握着苹果来回轻轻地搓，这样表面的脏东西很快就能搓干净，然后再用水冲干净，就可以放心吃了。','2014-10-23 12:20','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师秦慧玲','true','【身体的10大健康预警信号】1.头发脱落；2.黑眼圈；3.打鼾；4.乳房不对称；5.指甲增厚；6.呼吸异味；7.经常清喉咙；8.身材苹果状；9.经常大汗淋漓；10.皮肤经常出现大面积淤青。如果你身体出现以上症状，一定要放在心上，防患于未然。','2014-10-22 11:11','1','0','0','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师秦慧玲','true','【服避孕药忌吃柚】柚子是女性秋冬季常吃的水果，但服避孕药期间最好不要吃。美国的一项研究显示，服避孕药的女性如果在性生活后吃柚子，或者服避孕药前后短时间内喝柚子汁，发生意外怀孕的风险会大大增加。这是因为柚子会抑制人体酶。','2014-10-21 09:00','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师吕梅','true','#喝酒小常识#酒精对肝脏有直接毒性作用，吸收入血的乙醇在肝内代谢，造成其氧化还原状态的变化，从而干扰脂类，糖类和蛋白质等营养物质正常代谢，同时也影响肝脏的正常解毒功能。所以一方面控制饮酒，一方面不得不喝的时候一定提前吃点东西，比如说富含果胶的水果，含有丰富B族维生素的谷类，薯类等。','2014-06-25 18:34','1','1','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师吕梅','true','#补钙的食物都有哪些#补钙的食物有很多，但是各有利弊。1.首选牛奶，钙的最佳来源；2.骨粉含钙丰富，但吸收率低，有金属残留问题；3.鸡蛋壳钙也丰富，但吸收不好，有沙门氏菌；4.骨头汤钙含量极低，5.芝麻酱钙含量高，但脂肪也高；6.青菜钙含量也可以，有草酸，需焯水再吃。所以牛奶补钙快捷，方便！','2014-06-25 18:06','1','0','2','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师吕梅','true','#蔬菜怎么吃才健康#中国居民膳食指南规定，每天每人除蔬菜的量要达到300g~~500g,因为蔬菜当中含有丰富的矿物质，维生素和植物化学物质，是人体必不可缺的营养物质，但是蔬菜中的维生素和矿物质活性强，特别容易流失，所以烹调时要注意以下几点：1；先洗后切：2：急火快炒：3：开汤下菜；4：炒好即食。','2014-06-25 17:54','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师吕梅','true','#喝酒的小常识#适量喝酒会对身体有一定的保健作用，但是喝酒过量会对身体造成伤害，但是每天喝多少呢？男性：每天高度白酒不超过一两，红酒250毫升，啤酒750毫升，女性：每天38度白酒不要超过一两，红酒150毫升，啤酒450毫升。希望大家喝酒时一定要注意量的把控，身体健康是最大的幸福！','2014-06-25 17:22','0','6','0','2','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师吕梅','true','#水溶性膳食纤维#它主要包括果胶、树胶、黏胶和部分半纤维素等。主要存在于水果和蔬菜中。它对人体的作用：1、延缓胃排空时间。2、有利于“三高”人群（它可结合营养素如脂肪、胆固醇等排出体外）具有吸附性。3、过量容易引起营养素的缺乏。物无美恶过则为灾。适量摄取，保健康。','2014-06-11 10:35','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('米雪儿dey','false','　　梨，味甘微酸、性寒、无毒。可以止咳、润肺凉心、消炎降火、解痰毒酒毒之功效。梨子含有充足水分、和维生素A、B、C、D、E并能提供纤维素和钙、磷、铁、碘、钾等微量元素。饭后吃梨有益健康，而加热的梨汁含有大量的抗癌物质多酚，从而有效预防癌症，梨含果酸促进胃肠蠕动保护肝脏、适合饮酒的人 　','2015-01-30 08:24','5','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('慕付青爱健康','false','【秋吃莲子健脾补肾】莲子有很好的滋补作用，秋天食用有养心安神、健脾补肾、止泻固精等功效。红莲子口感较硬，补肾、补血效果更好；白莲子口感香糯，健脾的功效更强。除了莲子粥、红枣银耳莲子汤、银耳莲子羹等传统食用方法，用莲子心泡茶还能去心火，莲子泡酒则具有固肾的效果。','2014-09-20 08:49','0','1','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('慕付青爱健康','false','#晒晒你的健康餐#晚餐：黄豆面约70g，里面加了一个西红柿（约80个）、一个鸡蛋（食补约（55g）、一把空心菜（约170g）、一小片豆腐干（约30g）、一点紫菜、一点虾米，炝锅用调和油半勺、起锅滴几滴香油。颜色搭配红黄白绿黑，营养搭配七大营养素基本都全面了。晚餐后1小时出去运动。亲们，你的晚餐呢？','2014-05-04 19:32','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('慕付青爱健康','false','#晒晒你的健康餐# 我的早餐：自己煲的营养粥（大米、薏米、红豆、花生、核桃仁、银耳、百合、枸杞、红枣），外加一袋酸奶、一个香菇青菜包子。','2014-04-23 16:08','1','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('慕付青爱健康','false','【从小事做起，让你健康长寿】 ①改变饮食模式。多喝水；避免暴饮暴食；饭吃七分饱。 ②常规锻炼，规律睡眠。 ③蹲姿排便。减少肠癌和痔疮的发病。 ④摄入抗氧化剂。 黑巧克力、红葡萄酒。 ⑤减少压力。感觉压力大时做深呼吸；尽可能地消除恐惧感；生活有目标。 ⑥常用脑。','2014-03-12 20:44','0','1','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师付晓旭','false','【酒有什么营养价值？】酒类的营养价值体现在能提供能量。白酒中可检出微量氨基酸，葡萄酒和啤酒中可有一些蛋白质、肽类、氨基酸、糖类、碳水化合物和花青素，花青素有抗氧化作用，健康的人每天饮少量的饮用葡萄酒对身体有一定的好处。不论是什么酒都应该限量饮用。以免伤身。','2015-03-19 23:37','0','6','0','1','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师付晓旭','false','【饭后一杯酸奶有助消化吗?】酸奶中的益生菌可以促进肠道健康，帮助消化。但是益生菌、益生元这一类的产品，质量标准和法定检测都还处于真空地带。酸奶中的益生菌的数量和活性我们无从得知，长期坚持喝一定量的酸奶，会有利于肠道健康。2、酸奶本身也含有能量，如果吃的过饱再喝酸奶，对身体无益。','2015-03-11 23:23','0','0','6','4','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师付晓旭','false','【早餐奶比普通奶更有营养吗?】早餐奶目前没有专门的国家标准,各地奶源也尽不相同,早餐奶主要是生牛乳,白糖,各种谷物,香精,添加剂等,口味上要比普通奶更好一些,,因为其他配料的稀释,早餐奶的营养成分要低于普通奶,蛋白质和脂肪含量普遍降低.。','2015-03-11 23:17','0','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师付晓旭','false','【塑化剂是什么】塑化剂是一种塑料软化剂，添加后可让微粒分子更均匀散布，因此能增加延展性、弹性及柔软度，常作为塑料制品原料，可能存在于矿泉水瓶子或白酒。①长期、大量摄入塑化剂损害男性生殖能力②可促进女性第二性征发育③造成基因毒性伤害人类基因④影响消化免疫系统 诱发肝癌、造成畸形儿。','2015-03-02 23:35','0','1','0','4','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师付晓旭','false','【牛磺酸】牛磺酸的功能主要有：①促进脑细胞DNA、RNA的合成，促进脑细胞尤其是海马细胞结构和功能的发育，增强学习记忆能力。②改善视神经功能③抗氧化作用④促进脂类物质消化吸收⑤免疫调节作用。在坚果和豆科植物的籽实如黑豆、蚕豆、嫩豌豆、扁豆及南瓜籽中也含有较多的牛磺酸。','2015-02-27 15:00','1','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师付晓旭','false','【高脂血症的膳食指导】调整饮食和改善生活方式是各种高脂血症治疗的基础，即使是正在进行药物治疗者1、食物多样谷类为主粗细搭配2、多吃蔬菜水果和薯类3、常吃奶类豆类或其制品4、经常吃适量鱼禽蛋瘦肉，少吃肥肉和荤油5、保持能量摄入，并增加运动，防治超重和肥胖6、吃清淡少盐的膳食，多喝茶','2015-02-24 23:12','2','0','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('王金凤营养师','false','#家庭预防食品容器和包装材料的建议# 1、购买的时候应该注意容器和材料的质量2、金属、陶瓷等不能用来长时间存放酸性食物（如醋、果汁等）塑料容器不宜长时间存放酒精、油脂等3、买回来的包装食品尽量在低温下存放4、禁止拿非包装材料来包装食品（如：报纸）5、破罐子之类的不完整容易不宜用于保存食物','2015-03-09 11:22','1','1','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('王金凤营养师','false','#早上吃水果才好嘛？#当然不是，显然这又是一个谣言。对于健康的人来说，吃水果并没有什么时间上的限制。在食物总能量不超标的基础上，只要你的胃感觉舒服、没有不适，想吃水果就吃吧，早上、中午或者晚上，都是可以的。而且建议吃水果在两餐之间吃，这样可以起到很好的饱腹感。','2014-12-19 14:46','4','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('王金凤营养师','false','#冻伤的香蕉吃不吃？#冻伤香蕉和碰伤苹果的结局是相似的——都是细胞的破损。如果没有细菌抢着去抢占这些破损细胞的营养，这类坏果子也是相对安全的，只是口感会差一点。不过由于细胞的破损，氨基酸、糖和无机盐等从细胞中流出来，给致病微生物、真菌的生长提供了良好条件，所以及时发现及时吃掉。','2014-12-19 13:06','3','0','0','3','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('王金凤营养师','false','#哪些食物不容易掉色#类胡萝卜素是一类天然色素，常见的类胡萝卜素有胡萝卜、南瓜、红薯和深绿色蔬菜中富含的α-胡萝卜素、β-胡萝卜素和叶黄素；番茄和红瓤西瓜中富含的番茄红素；玉米中的玉米黄素，还有柠檬、桔子、橙子、柚子等也含有大量的类胡萝卜素类色素。虾蟹中煮熟了会变红的虾青素也属于。','2014-12-19 12:31','9','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('王金凤营养师','false','#在外点餐怎么做到健康#掌握一鱼肉、一蔬菜、一个荤素搭配菜的原则，健康点菜还是比较容易的。在此基础上，还要注意食材原料不重复，比如不能两个菜都含有猪肉，或者两个菜都是鸡肉做的。在味型方面也要多样一点，有浓，有淡。这样搭配出来的菜，肯定能调和众口，大受欢迎。不信就试一试。','2014-12-12 18:15','1','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('陈永辉医生','true','【儿童零食分等级】1.限制级，典型代表：油条、方便面、膨化食品类油炸烧烤食物。这类食物多采用不健康烹调方式制作，要限制摄入；2.适当食用级，典型代表：饼干、点心类，这类食物可适当食用但不宜多吃；3.可经常食用类，典型代表：酸奶、水果、黄豆、地瓜，这类食品较健康。','2014-06-01 14:02','1','0','1','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('陈永辉医生','true','【中医保健养生顺口溜】 早起一杯水，清肠又润胃。常吃一点蒜，抗癌又保健。多食一点醋，不用上药铺。多吃一点姜，益寿保健康。每天一苹果，医生远离我。一顿要吃伤，十顿要喝汤。炼出一身汗，小病不用看。练就一身功，年老筋骨通。无病一身福，长寿万事足。要活一百多，心胸要宽阔。','2013-10-02 19:20','1','0','0','3','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('陈永辉医生','true','小儿咳嗽在临床很常见，中医治疗首先需分清虚实，实证有风寒、风热、痰热、痰湿的区别，虚证有气虚、阴虚之不同，对于不同的证，选用相应的药物才能取得满意的效果，如果用药不对证，往往非但无益，反而有害！常常有些家长一见孩子咳嗽，就喝冰糖梨、蜂蜜梨、川贝梨，是错误的！切记！！切忌！！！','2013-06-02 10:26','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('车品觉','true','品觉晨读(来源:新浪科技) 知情人士透露，苹果计划将Force Touch技术引入新一代iPhone。这并不是第一次传出新iPhone将引入Force Touch技术的传闻。早在2013年就有传言称，苹果计划改善iPhone的压力传感器。 O网页链接','2015-03-12 05:56','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('阅湖9姐','false','开饭啦 包子，大包子，猪肉马齿苋大包子 外加鲜玉米皮做的，无油不沾屉布，健康环保从每一餐开始','2015-03-22 19:42','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('光耀医生','true','酒尽量少喝 毕竟药物有肝脏毒性 但不可临时停药 柚子没有所谓 N翟医生：您好！二型糖尿病人（依靠运动和饮食，血糖、糖化血红蛋白一直控制很好），因总胆固醇和低密度脂蛋白胆固醇指标偏高，现每日10mg辛伐他汀片，请问：1. 服药期间，能偶尔喝酒吗？比如每日不多于1瓶的啤酒，或者每日不多于2两的红酒。2. 如果某晚有饭局要少量喝酒，那么当晚就不吃药了，这样好吗？3. 服药期间，能吃柚子吗？比如每天吃少量的沙田柚不超过一两瓣。期待您的专业指导，非常感谢！  ','2015-02-11 13:06','0','3','0','1','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师徐光芒','true','#坚持锻炼一百天# 第3轮第2天，今天坐高铁 从广州南到上饶，下车就被姐夫接去和朋友喝酒，盛情难却，喝了不少啤酒 和高油高盐的菜，必须通过消耗掉才行！回到家踢毽球30分钟，做俯卧撑30个，衣服湿了。 @王旭峰营养师 @坚持锻炼一百天 @首都保健营养美食学会','2015-02-15 22:24','0','1','0','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师徐光芒','true','#失眠如何对症饮食#之一【失眠原因】1.精神上的不安，如忧郁、紧张、压力等；2.缺乏维生素、矿物质引起身体营养失衡；3.酒精、咖啡 浓茶等刺激物过量等都会严重干扰睡眠质量，引起失眠。','2015-01-22 09:01','0','1','0','1','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师徐光芒','true','#蔬菜之洋葱#功效之一【预防和改善糖尿病】用200克左右洋葱，加鱼腥草干品20克（药店买），放600毫升水煮至剩300毫升，然后一次或两次喝完，对预防和改善糖尿病特别有效！','2015-01-09 11:15','3','0','0','3','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师徐光芒','true','#认识植物营养素#之【植物营养素的来源】 水果和蔬菜中含有丰富的植物营养素，其它植物性食物，如谷物、豆类、坚果和茶中也含有植物营养素。植物营养素来源于植物，同时也能对植物产生积极的作用，主要有两方面：1.赋予食物鲜艳色彩的色素 通过食物的颜色，你就可以辨别富含植物营养素的食物。','2015-01-05 18:59','2','0','0','1','wine');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师徐光芒','true','#认识植物营养素#【植物营养素的概念】植物营养素对人体的保健功能日益引起关注。我们来了解一下：可食性植物，如蔬菜、水果、谷类等，除富含维生素、矿物质和纤维素外，还含有众多植物自身合成、且对人体有益的天然有机化合物，例如原花青素、槲皮素、番茄红素等，营养学家称之为植物营养素。','2015-01-03 11:45','3','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('Anna-MsLi','false','千万不要用生果做脸！很多姐妹喜欢用蔬菜、水果自制面膜，因为蔬菜水果一旦切开了，氧化比吸收还快！会令斑更严重和色素沉淀！皮肤越来越干！皮肤只能吸收适合的分子颗粒，水果蔬菜的营养需要经过胃酸消化后，通过血管输送养分，我们的表皮层没有血管。新鲜的蔬菜和水果还是吃了吧！','2015-03-11 09:06','8','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('63号C家','false','消耗奶油，怕过期了浪费，做出来味道还不错，就是火大了，还有点回缩，估计蛋白没打发好 ，刚少了一张步骤','2015-03-15 13:43','0','0','1','1','milk');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('海青营养师','true','亲们 过年有没有胖三斤啊？ 今天就来跟大家讲讲肥胖的九大危害。ps:广告一下，最近和营养师朋友一起做公众号“青柠檬健康生活管家”，发跟营养相关的文章，第一个系列是关于如何健康减肥的。我们还会在线答疑，有任何问题也可以咨询。欢迎大家关注[拥抱][拥抱][拥抱] O 肥胖的九大危害','2015-03-06 20:09','1','0','0','2','fruit');
