insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('营养师邢虹','true','#营养小知识# 【高血压人群健康口诀】少吃肥肉和内脏，多选富纤食物和果蔬，高钙钠盐是关键，浓茶咖啡不沾杯。 @梨香水韵 @营养师魏琳琳 @营养师刘鹂静 @传麒陪你学营养 @营养师土星 @营养师李柏春 @张痘痘不长豆豆 @_l95 @wtnbnt @营养烹饪何小伟','2015-01-07 15:01','1','0','0','1','fruit');
