insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('孕妈咪热淘榜TOP','false','【孕妈可以使用什么护肤品？】1、选用低泡或者无泡的洁面乳；2、安全性能高、无香精香料成分添加的；3、含有维生素C的产品；4、含有低浓度果酸、活肤锌、葡萄糖酸锌等成分的净化类产品；5、特级橄榄油可以用来按摩肌肤；6、月见草油、玫瑰果油、VE油、葡萄籽油等几种混合使用。','2015-03-02 15:39','1','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('全球母婴欢乐汇','false','【儿童时期多吃粗粮 长大后心脏好】研究发现,儿童多吃富含纤维素的蔬菜和全谷物等食物,有助于降低日后心脏病危险.专家建议,要使儿童心脏更健康,每天应该吃3份水果、3份蔬菜和两份全谷食物.全谷食物主要包括:全麦面粉、全麦面包、燕麦片、糙米和大麦等.','2015-03-01 08:56','3','0','0','1','fruit');
