insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('健康饮食','false','#健康饮食#【3款宝宝止咳粥】1.梨粥：用梨两个，洗净连皮带核切碎，加粳米100克煮粥。2.银耳粥：银耳10克，发泡后加大米100～150克同煮，加适量蜂蜜搅匀即可。3.麦门冬粥：用麦门冬20～30克，煎汤取汁，再以粳米100克煮粥待半熟，加入麦门冬汁和冰糖适量同煮。','2015-03-19 18:30','2','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('健康饮食','false','【失眠伤不起啊！8种食物养颜又助眠】1、香蕉让肌肉放松。2、菊花茶凝神静气。3、温牛奶镇静安神。4、蜂蜜有助于睡前放松。5、土豆清除妨碍睡眠的化合物。6、燕麦片诱使产生褪黑素轻松助眠。7、杏仁催眠又能保护心脏。8、 亚麻籽富含不饱和脂肪酸。如果你身边有睡眠质量不好的人，转给他吧！','2015-03-19 09:30','1','0','1','1','milk');
