insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('李晓丽营养师','true','自制【草莓果酱】——色泽红润、口感纯正，无任何增稠剂、甜味剂、色素的添加，吃起来安全健康无污染！ 可以抹在自制的面包片上，做成果酱面包；做好的自制酸奶，加上一勺自制果酱，即增添了色彩又丰富了口感。 健康美食，大家赶快做起来！','2015-03-13 18:30','1','0','1','5','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('慧莹营养师','true','3.8，属于女同胞的节日！大亚湾基地各餐厅都在行动~健康而漂亮的玫瑰花卷，所有花卷原料天然，都出自面点师傅纯手工制作。美眉们在早餐时段将会收到紫薯玫瑰花卷/南瓜玫瑰花卷1个喔~祝大家美丽永驻、健康常在 @大亚湾核电','2015-03-06 20:31','1','0','0','2','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('慧莹营养师','true','发表了博文《宝宝辅食添加7点小贴士》初到世间的宝宝，对食物既新鲜又陌生。天然的米面糊、新鲜的蔬果汁（泥）、蛋黄泥、鱼泥等辅食的添加，是宝宝一天天健康成长的营养保证。让宝宝慢慢感受原始食物的最初味道，循 O宝宝辅食添加7点小贴士','2015-02-10 21:18','1','0','0','1','fruit');
