insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('化妆品独立评论陈暖','false','抑菌要适度，抗生素具有抑制线粒体的作用，线粒体是人体的发电站，当线粒体功能减弱后细胞活动和合成能力都会受到抑制，醉酒后感觉疲劳就是燃烧酒精消耗了大量能量，线粒体不能及时补充能源。为何抗生素影响能量呢？因线粒体进化过程中吞噬了细菌基因。过度抗生素控痘就会造成皮肤干燥，恶化，耐药菌。','2015-03-17 08:30','0','1','0','1','milk');
