insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤中心','false','【果醋绿豆收缩毛孔面膜】材料：绿豆粉、苹果醋、番茄泥做法：把2小匙绿豆粉加入到1小匙苹果醋中，然后加入事先捣碎的番茄泥，充分搅拌后就可以敷在脸上了，能够有效收缩毛孔。功效：这款自制面膜能够促进肌肤的新陈代谢，帮助收缩粗大的毛孔，让皮肤变得更加细腻有弹性，适合中性和油性的皮肤。','2015-02-27 19:52','4','0','0','1','fruit');
insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('美容护肤中心','false','【番茄蜂蜜汁减肥】材料：番茄2个，蜂蜜1大匙，冰块适量。 做法：番茄去蒂，洗干净切成小块。将番茄放入榨汁机里榨成汁再倒入杯里，加 入蜂蜜，冰块搅拌均匀即可。点评：番茄含有的果胶、茄红素、纤维可以降低人体对热量的吸收，还有清理肠胃、美白的功效。','2015-01-02 20:02','5','0','0','1','fruit');
