insert into `weiboinfo` (`name`, `vip`, `content`, `time`, `fruitnum`, `winenum`, `milknum`, `safetynum`, `category`) values('39健康网','true','【最养男人的五种食物】1.大蒜、韭菜等葱属类蔬菜。杀菌、降低前列腺癌。2.西兰花、白萝卜等十字花科蔬菜。3.沙丁鱼、三文鱼等富含欧米伽3脂肪酸的食物。对心血管有益。4.西红柿等富含番茄红素的食物。保护前列腺。5.牡蛎、瘦肉等富含锌的食物。提高精子质量。','2015-03-18 16:30','6','0','0','1','fruit');
